node.doctext:
[%
return if $$USER{node_id} == $HTMLVARS{guest_user};

my $str = '<form method="post">
<input type="hidden" name="node_id" value="'.$$NODE{node_id}.'" />';

my $inputname = '';

if($query->param('ns_user'))
{
	$inputname = $query->param('ns_user');
}
else
{
	$inputname = $$USER{title};
}

$str.="<b>Don't want your nodeshells searchable? You can turn it off in [User Settings].</b><br /><br />";

$str.= 'Look up user:<br /><input type="text" name="ns_user" value="'.$inputname.'">&nbsp;<input type="submit" value="View Nodeshells" /></form><hr /><br />';

my $nsUser = getNode($inputname, 'user');

return $str.'No such user "'.$inputname.'"<br />' unless $nsUser;


my $nsVars = getVars($nsUser);

return $str.linkNodeTitle($$nsUser{title}).'\'s nodeshells are not searchable.' if ( ($$nsVars{hideNodeshells}) && (!isGod( $USER )) && (!($$nsUser{title} eq $$USER{title})) );


my $csr = $DB->sqlSelectMany('node_id, title, (select count(*) from nodegroup where nodegroup_id=node.node_id) as groupcount', 'node', 'type_nodetype='.getId(getType('e2node')).' and author_user='.$$nsUser{node_id}.' having groupcount=0', "ORDER BY node.title");

$str.=htmlcode('openform');

$str.= '<p>(Be sure to check out [your filled nodeshells], too.)</p> <p><strong>'.$csr->rows.'</strong> nodeshells created by '.( ($$nsUser{title} eq $$USER{title} ) ? "you" : $$nsUser{title} ).' which do not contain writeups:</p>
<ul>
';


my $icsr = $DB->getDatabaseHandle()->prepare('SELECT to_node FROM links WHERE linktype=1150375 AND from_node=?');

my $ctr;
my @nodes;
while(my $row = $csr->fetchrow_hashref) {

  if ($query->param("delete_$$row{node_id}"))
  {
  my $nodeToDelete=getNode($$row{node_id});
  if(($nodeToDelete{author_user}==$$USER{user_id}||isGod( $USER ))&& $$nodeToDelete{groupcount}==0)
    {
    $DB->sqlDelete('nodegroup', "nodegroup_id=$$row{node_id}");
# is it better to call nukeNode with a member of the admin group to achieve this?
    }
  }

  $str .= '<li>' . linkNode($$row{node_id});
  if($icsr->execute($$row{node_id}))
  {
    if($icsr->rows>0)
    {
      $str .= ' - <b>Firm linked to:</b> ';
      $ctr = 0;
      while(my $irow = $icsr->fetchrow_hashref)
      {
        if ($ctr > 0)
        {
          $str .= ', ';
        }
        $str .= linkNode($$irow{to_node});
        $ctr++;
      }
    }
    elsif ($$USER{node_id}==$$nsUser{node_id}||isGod( $USER ))
    {
      $str .= $query->checkbox( -name => "delete_$$row{node_id}", -label => '',   -value => 'off');
    }
  }
}

$str.='</ul>';
$str.=htmlcode('closeform');
return $str;
%]
