node.doctext:
[%
my $isGuest = ($$USER{node_id}==$HTMLVARS{'guest_user'});
return "<p>Undifferentiated from the masses of the streets, you fall between the cracks yourself.</p>" if $isGuest;

my $rowCtr = 0;
my $unVotable = getVars(getNode('boring heaven nodes', 'setting'));
my ($title, $queryText, $rows);
my $count = 300;
my $maxVotes = int($query->param("mv"));
my ($minRep, $repRestriction, $repStr) = (undef, '', '');

if ($maxVotes <= 0)
{
   $maxVotes = 5;
}
if (defined $query->param("mr") && $query->param("mr") ne "") {

  $minRep = int($query->param("mr"));
  if ($minRep > 5 || abs($minRep) > ($maxVotes - 2)) {
     $minRep = undef;
  }
  if (defined $minRep) {
     $repRestriction = "AND reputation >= $minRep";
     $repStr = " and a reputation of $minRep or greater";
  }
}

my $dbh = $DB->getDatabaseHandle();
return 'No database handle!' unless $dbh;

my $str = '<p>These nodes have fallen between the cracks, and seem to have gone unnoticed. This page lists <em>up to</em> 50 writeups that you haven\'t voted on that have fewer than '.$maxVotes.' total vote(s)'.$repStr.' on E2. Since they have been neglected until now, why don\'t you visit them and click that vote button?</p>

<form method="get"><div>
<input type="hidden" name="node_id" value="'.getId($NODE).'" />
<b>Display writeups with ';

my @mvChoices = ();

for(my $i=1;$i<=10;$i++)
{
   push @mvChoices, $i
}

$str .= $query->popup_menu('mv', \@mvChoices, $maxVotes);
$str .= ' (or fewer) votes and ';

my %mrLabels = ();
my @mrValues = ();

for(my $i=-3;$i<=3;$i++)
{
   $mrLabels{$i} = $i;
   push @mrValues, $i;
}

$mrLabels{''} = 'no restriction';
push @mrValues, '';

$str .= $query->popup_menu(-name => 'mr',
                              -labels => \%mrLabels
                            , -default => $minRep
                            , -values => \@mrValues
                          );
$str .= ' (or greater) rep.';

$str .= '</b>
<input type="submit" value="Go" />
</div></form>

<table width="100%">
   <tr>
   <th>#</th>
   <th>Writeup</th>
   <th>Author</th>
   <th>Total Votes</th>
   <th>Create Time</th>
   </tr>';

$queryText = <<ENDQUERY;

SELECT title, author_user, createtime, writeup_id, totalvotes
  FROM writeup
  JOIN node
    ON writeup.writeup_id = node.node_id
  LEFT OUTER JOIN vote
    ON vote.vote_id = node.node_id AND vote.voter_user = $$USER{user_id}
  WHERE
    node.totalvotes <= $maxVotes
    $repRestriction
    AND vote.voter_user IS NULL
    AND wrtype_writeuptype <> 177599
  LIMIT $count

ENDQUERY

$rows = $dbh->prepare($queryText);
$rows->execute() or return $rows->errstr;

while(my $wu = $rows->fetchrow_hashref)
{
   $title = $$wu{title};
   if($title =~ /^(.*?) \([\w-]+\)$/) { $title = $1; }
   $title =~ s/\s/_/g;
   if (!$$unVotable{lc($title)} && !htmlcode('flagged', $$wu{writeup_id}, 'unvotable'))
   {
      $rowCtr++;
      if ($rowCtr % 2 == 0)
      {
         $str .= '<tr class="evenrow">';
      }
      else
      {
         $str .= '<tr class="oddrow">';
      }
      $str .= '<td style="text-align:center;padding:0 5px">'.$rowCtr.'</td>
         <td>'.linkNode($$wu{writeup_id}, '', {lastnode_id=>0}).'</td>
         <td>'.linkNode($$wu{author_user}, '', {lastnode_id=>0}).'</td>
         <td style="text-align:center">'.$$wu{totalvotes}.'</td>
         <td style="text-align:right">'.$$wu{createtime}.'</td>
         </tr>';
   }
   last if ($rowCtr >= 50);
}

if ($rowCtr == 0)
{
   $str .= '<tr><td colspan="3"><em>You have voted on all '.$count.' writeups with the lowest number of votes.</em></td></tr>';
}
$str .= '</table>
   <p style="text-align:right">Bugs to [in10se]</p>';

return $str;
%]
