node.doctext:
[%
return unless ($$USER{title}eq'Oolong');

use Everything::NodeBase;

my $str;
my $clickyClicky;
$clickyClicky=$query->param( 'clicky' )||'noClicky';

$str.=linkNode($NODE, 'CLICKY', { 'clicky'=>'click'}) unless ($clickyClicky eq'click');

#my $csr = $DB->sqlSelectMany("node_id","node", "type_nodetype=1254859");


if ($clickyClicky eq'click') {
  $DB->sqlUpdate ("node", { -hits => '0' }, "hits > 0 AND type_nodetype=117 ORDER BY hits DESC LIMIT 100") ;
  #return $str;
}


my $csr = $DB->sqlSelectMany("node_id,hits", "node", "hits > 0 AND type_nodetype=117 ORDER BY hits DESC LIMIT 100");
#$csr->prepare();
#$csr->execute();
#3) You have to use fetchrow (or  better still, fetchrow_hashref) on the cursor)

$str.="<ol>";

while (my $n = $csr->fetchrow_hashref) {
  $str.="<li>".linkNode($$n{node_id})." - $$n{hits} hits</li>";
}

$str.="</ol>";


return $str;

%]
