node.doctext:
[%
use DateTime;

return "New user creation disabled from dev server. "
	. "If you want in, go to e2 and ask a member of [edev] to let you in."
	if $HTMLVARS{isDevServer} ;

my $str;
my @addrs = htmlcode('get ips');

#for debugging, let logged in admins have debug checkbox
my $isRoot = exists $HTMLVARS{group_gods}{getId($USER)};

### Generate the CAPTCHA (the keys must be different for .com, .net, and .org)

my $reCAPTCHA;
use Captcha::reCAPTCHA;
my $captcha = Captcha::reCAPTCHA->new;
my $reCAPTCHAPublicKey = '';
my $reCAPTCHAPrivateKey = '';

if($CONFIG{use_captcha} != 0)
{
  my $server = $ENV{HTTP_HOST};
  if($server =~ m/everything2\.com$/){
  	$reCAPTCHAPublicKey = '6LfsXAIAAAAAABqYVncS2qc2o7P8rHoxOH_3QH2t';
	$reCAPTCHAPrivateKey = '6LfsXAIAAAAAAM4PtmzdbhTmpiieSNJ2YUBnjPRd';
  }elsif($server =~ m/everything2\.net$/){
	$reCAPTCHAPublicKey = '6LfuXAIAAAAAAIeVU24N_zB8Ez0kK9rzNeOKx8HX';
	$reCAPTCHAPrivateKey = '6LfuXAIAAAAAAAA9bE5SO5c60BH69t6lT4B4ShY2';
  }elsif($server =~ m/everything2\.org$/){
	$reCAPTCHAPublicKey = '6LfvXAIAAAAAAI2xn_O42yiTXbB_e-9gA6AfXVi8';
	$reCAPTCHAPrivateKey = '6LfvXAIAAAAAAAOPw0yZVfmK4Nviw-KTiLfMdq_g';
  }
  $reCAPTCHA = $captcha->get_html( $reCAPTCHAPublicKey );
}else{
  $reCAPTCHA = "";
}

### Generate the Login HTML stub
local *generateLogin = sub {
	$query->start_form
	.'<input type="hidden" NAME="op" VALUE="newuser" />'
	.$query->hidden('node_id', getId($NODE))
	.'<div><strong>Username</strong><br />'
	.'<input type="text" NAME="nu_name" maxlength="20" VALUE="" />'
	.'</div><br />'
	.'<div><strong>Real Name</strong><br />'
	.$query->textfield('nu_realname', '', 20)
	.'</div><br />'
	.'<div><strong>Email Address</strong><br />'
	.$query->textfield('nu_email', '', 20)
	.'</div><br />'
	.'<div><strong>Password</strong><br />'
	.$query->password_field('nu_password', '', 20)
	.'</div><br />'
	.'<div><strong>Password, again</strong><br />'
	.$query->password_field('nu_password2', '', 20)
	.'</div><br />'
	.'<div><strong>Show your Humanity</strong><br />'
	.$reCAPTCHA
	.'</div><br />'
	.$query->submit('sexisgood', 'submit')
	.$query->end_form;
};

my $testCreateOnly = 0;


local *debugLog = sub {
	my $msg = $_[0] || '';

	#internal host from [admin searchform] (htmlcode)
	my $servername = `hostname`;
	chomp $servername;
	$servername =~ s/\..*//g;

	# Grab all IP addresses and log all of them
	my $addr = join ' - ', @addrs;
	#address from general container
	#maybe use later for acount-creation-abuse ban

	$msg .= ' on '.$servername." as $ENV{SERVER_NAME}";
	$msg .= ' IP '.$addr;
	$msg .= ' (testing only)' if $testCreateOnly;
	$msg = $dbh->quote($msg);
	$DB->sqlInsert('seclog', {
		'seclog_node'=>$$NODE{node_id},
		'seclog_user'=>$$USER{user_id},	#probably just guest user
		'seclog_details'=>$msg,
	});
};

my $errorMessage = '';

if ($query->param('op') eq 'newuser') {

	my $doNotSend = 0;
	my $realname = $query->param('nu_realname');
	$realname =~ s/(\\g|;|"|'|`)//g;  # No SQL injection here.
	#my $realname = '';

	my $email = $query->param('nu_email');
	$email =~ s/\s+//g;
	#if * is 1 or more characters, checking for *@*.*
	# local address portion may contain alpha-numeric and a bunch of punctuation
        # host portion may contain only alphanum, dots, and dashes
	unless ($email =~ /^([A-Za-z0-9!#\$%&'*+\/=?^_`{|}~\.-]+\@[a-zA-Z0-9\.-]+\.[a-zA-Z]{2,4})$/) {
		$errorMessage = "You must provide a valid email address.";
	}

	$email = $1;

	if ($email =~ /^\./ || $email =~ /\.\@/ || $email =~ /\.\./) {
		$errorMessage = "You must provide a valid email address.";
		$email = undef;
	}


	my $name = $query->param('nu_name');
	$name =~ s/\t//g;

##################
#CHECK THE CAPTCHA

if($CONFIG{use_captcha} != 0)
{
  my $challenge = $query->param('recaptcha_challenge_field');
  my $response = $query->param('recaptcha_response_field');
  my $result = $captcha->check_answer( $reCAPTCHAPrivateKey, $ENV{'REMOTE_ADDR'}, $challenge, $response );

  unless ( $result->{is_valid} ) {
	debugLog('attempted create user ['.encodeHTML($name,1).'], but failed CAPTCHA.');
	$errorMessage="You did not correctly type the two reCAPTCHA words.  Please try again.";
  }
}


####################
#CHECK IF THE USER EXISTS

	my $olduser;
	my @allUserTypes = ('user', 'usergroup', 'superdoc', 'restricted_superdoc', 'nodetype', 'document', 'superdocnolinks', 'fullpage');
	foreach(@allUserTypes) {
		$olduser = getNode($name, $_) unless $olduser;
	}

	if($olduser) {
		debugLog('attempted create user ['.encodeHTML($name,1).'], but exactly matches: ID '. getId($olduser) .', type '.$$olduser{type_nodetype});
		$errorMessage = "Sorry, '".linkNode($olduser)."' already exists.  Pick a variation, or try another name...";
	}



###################
#CHECK FOR "_" vs " " CONFLICTS

	my $match=lc($name);
	$match=~s/[_ ]/_/g;
	$match=~s/\\/\\\\/g;

	my $match2 = lc($name);
	$match2 =~ s/[_ ]/[_ ]/g;
	$match2 =~ s/\\/\\\\/g;

	my $closeuser = 0;
	foreach(@allUserTypes) {
		$_ = 'type_nodetype='.getId(getType($_));
	}
	my $csr =$DB->sqlSelectMany('title','node','title like '.$dbh->quote($match).' AND (' . join(' OR ',@allUserTypes) . ')');
	while(my ($u) = $csr->fetchrow) {
		next unless $u =~ m/^$match2$/i;
		$closeuser = 1;
	}

	if($closeuser) {
		debugLog('attempted create user "'.encodeHTML($name,1).'", but close to another');
		$errorMessage = "Sorry, your username clashes with a username which already exists.  Pick a variation, or try another name...";  
	}



##############################
#USERNAME SANITY CHECKING

	unless ($name =~ m/\w/) {
		$errorMessage="Uh...  how about some more letters in that username?";
	}

	if($name =~ m/[\[\]\<\>\&\{\}\|\/\\]/) {
		$errorMessage="That username has a special character in it.  Try something else.";
	}

	if(($name =~ m/^\s/) or ($name =~ m/\s$/)) {
		$errorMessage="Sorry, your username may not have spaces at either end.";
	}

	if ($name =~ m/\_/ && $name =~ m/ /) {
		$errorMessage="Usernames cannot contain both spaces and underscores.";
	}

	if($name =~ m/\s\s/) {
		$errorMessage="Please don't use multiple spaces in a row.";
	}



##################
#MAKE SURE THEY ENTERED THE SAME PASSWORD TWICE
my $passwd = $query->param('nu_password');
my $passwd2 = $query->param('nu_password2');
unless($passwd eq $passwd2){
	$errorMessage="Your passwords don't match.  Please try again.";
}



##################
#CHECK FOR REGISTRATION UNDER A BLACKLISTED IP
#  - make them jump through all the other hoops
#    before finding out if they're on the blacklist.


for my $address (@addrs) {

	my $ipblacklist_id =
		$DB->sqlSelect(
			'ipblacklist_id'
			, 'ipblacklist'
			, 'ipblacklist_ipaddress = ' . $DB->quote($address)
		);

	if($ipblacklist_id){
					debugLog('attempted create user ['.encodeHTML($name,1).'] from blacklisted IP - '.encodeHTML($address).'.');
					$errorMessage="We're sorry, but we can't accept new user signups from this IP address.";
	}

}



##################
#CREATE "ERROR" IF ONLY TESTING

	if (($errorMessage eq '')&&($testCreateOnly)) {
		$errorMessage= 'You would be able to create user='.$name.', email='.$email.'.';
	}



##################
#NO ERROR? CREATE USER

	if ($errorMessage eq '') {
		debugLog('success create user ['.encodeHTML($name,1).']');

		### Generate an Authentication Hash
		my @chars = ( 'A' .. 'Z', 'a' .. 'z', 0 .. 9);
		my $validationkey = join('', @chars[ map { rand @chars } ( 1 .. 25 ) ]);

		### Create the User Node
		my $NEWUSER = $DB->insertNode($name, $DB->getType('user'), -1, {
			'realname' => $realname,
			'email' => $email,
			'nick' => $name,
			'passwd' => $passwd,
			'validationkey' => $validationkey
		});
		getRef $NEWUSER;
		$$NEWUSER{author_user} = getId ($NEWUSER);
		$DB->updateNode($NEWUSER,-1);
		$HTMLVARS{newuser} = getId($NEWUSER);

		map {
			$DB->sqlInsert(
				"iplog"
				, {iplog_user => $$NEWUSER{user_id}
				, iplog_ipaddy => $_}
			)
		} @addrs;
		my $addr = join ',', @addrs;

		### Save a few initial settings
		my $uservars = getVars( $NEWUSER );
		$$uservars{ 'showmessages_replylink' } = 1;
		$$uservars{ipaddy} = $addr;
		$$uservars{preference_last_update_time} = DateTime->now()->epoch();

		$$uservars{infected} = 1 if htmlcode('checkInfected') == 1;

		setVars( $NEWUSER, $uservars );

		### Put the new user on email suspension until he or she verifies his or her account
		$DB->sqlInsert("suspension", 
			{"suspension_user" => $$NEWUSER{node_id},  
			"suspension_sustype" => '1948205',        ### Email Suspention Type Node ID
			"suspendedby_user" => '113'}              ### Root's Node ID
		);


		### Send the User a Welcome Message
		my $msgText = "Hi there and welcome to E2! I hope you're enjoying the site. If you haven't already done so, I recommend reading both [E2 Quick Start] and [Links on Everything2] before you start writing anything. If you have any questions or need help, feel free to ask any editor (we all have a \$ next to our names in the Other Users list)";
		$HTMLVARS{msgparams} = {
			'author_id' => getId(getNode('Virgil','user')),
			'recipient_id' => $$NEWUSER{author_user},
			'message' => $msgText,
		};
		my $sendResult = htmlcode('sendPrivateMessage','');

		### Send the User a Welcome Email
		use Everything::MAIL;
		my ($n) = getNodeWhere({title=>'Welcome to the Everything2 community!'},
		 						$DB->getType('mail')
		);
		$$n{doctext} =~ s/\<name\>/$$NEWUSER{realname}/g;
		$$n{doctext} =~ s/\<user\>/$$NEWUSER{title}/g;
		$$n{doctext} =~ s/\<passwd\>/$$NEWUSER{passwd}/g;
		$$n{doctext} =~ s/\<user_id\>/$$NEWUSER{user_id}/g;
		$$n{doctext} =~ s/\<validationkey\>/$$NEWUSER{validationkey}/g;
		$$n{doctext} =~ s/\<site_name\>/$HTMLVARS{site_name}/g;
		$$n{doctext} =~ s/\<homenode\>/http:\/\/$ENV{SERVER_NAME}\/index\.pl\?node=$$NEWUSER{title}\&type=user/g;
		$$n{doctext} =~ s/\<site_url\>/http:\/\/$ENV{SERVER_NAME}\//g;
		$$n{doctext} =~ s/\<login\>/http:\/\/$ENV{SERVER_NAME}\/index\.pl\?node_id=$HTMLVARS{login_node}/g;
		$$n{doctext} =~ s/\<verify_email_url\>/http:\/\/$ENV{SERVER_NAME}\/index\.pl\?node_id=$HTMLVARS{verifyEmailAccount_node}/g;
		$$n{doctext} =~ s/\<servername\>/$ENV{SERVER_NAME}/g;
		node2mail($$NEWUSER{email},$n, 1);

		my $homenodeLink = linkNode($NEWUSER,"your homenode");
		$str.= <<__HERE__
		<div id='login_info' style='width: 600px; margin-left: 50px;'>

			<h3>Welcome, $$NEWUSER{realname}!</h3>

			<p>Your new user account has been created, and an email has been sent to the address you provided.  <strong>Check your email for an authentication code</strong>.  You'll need to click on that link before you start writing with us.  Save this email for future reference: it introduces you to our community and contains info on what Everything2's all about.</p>

			<p>After you have logged in, you may change your password by visiting $homenodeLink and hitting the <strong>edit user information</strong> link.</p>

			<p>The best ways to get started after logging in is to introduce yourself in our chat room (located in the sidebar on the right side of your screen) and [E2 Mentoring Sign-Up|sign up for a mentor] to help you grow your writing.</p>

			<h2>[Welcome to Everything|Welcome to Everything2]!</h2>

		</div>
__HERE__
;

		return $str;
	}

}


######################
#SHOW CREATION FORM

	$str.="<h3>Thanks for showing interest in Everything2!</h3>"
	."<p style=\"margin: 10px 50px; width: 600px; \"> Creating a new user account will allow you to contact writers on the site and submit writeups of your own on any subject you like.</p>";  

	$str.= "<h2 class='error_message'>$errorMessage</h2>" if $errorMessage;

	$str.= "<h3>Getting to know you</h3>";

	$str .= "<div id='login_form' style='width: 600px; margin-left: 50px;'>" .generateLogin()."</div>";

	$str .= '<h3>Important Information</h3>

	<div id="login_info" style="width: 600px; margin-left: 50px;">

	<h4>Email Privacy Policy</h4>

	<p>We are not going to send you junk email "member updates", sell your address to spammers, or display your email address for people to see. We collect your email solely so that you can retrieve your password in case you forget it.</p>

	<h4>No Spam or Advertising</h4>

	<p>If you plan on using our site to promote your web site, business, or what-have-you free of charge, please don\'t bother. It\'s not worth your time and it wastes ours, too. All it does is annoy the few site users who see your post during the five minutes that it takes our staff to notice it and delete it. We will also not hesitate to ban you for plugging your goods on our site. <b>ATTENTION FAKE PROFILERS AND FORUM SPAMMERS: AS OF APRIL 2010, NEW ACCOUNTS ADVERTISING ANY PRODUCT, SERVICE, OR WEB SITE (INCLUDING "PERSONAL" SITES AND BLOGS) IN THEIR PROFILE ARE SUBJECT TO SUMMARY AND INSTANT DELETION. THEIR DETAILS WILL ALSO BE SUBMITTED TO PUBLIC BLACKLISTS FOR OTHER WEB SITES\' USE.</B></p>

<h4>Underage users</h4>

<p>Everything2 may include member-created content designed for an adult audience. Viewing this content does not require an account. For logged-in account holders, Everything2 may display text conversations conducted by adults and intended for an adult audience. On-site communications are not censored or restricted by default. Users under the age of 18 are advised that they should expect to be interacting primarily with adults and that the site may not be considered appropriate by their parents, guardians, or other powers-that-be. Everything2 is not intended for use by children under the age of 13 and does not include any features or content designed to appeal to children of that age.</p>

	</div>';



	return $str;

%]
