node.doctext:
[%
  return unless isGod($USER) || $$USER{user_id} == getId(getNode("WolfDaddy", "user"));

  my $str = htmlcode("openform")."The user: <input type=\"text\" name=\"userperson\"><br> was introducted to E2 by <input type=\"text\" name=\"parentperson\"> (leave blank for \"\&lt;ETHER\&gt;\")<br>Comment: <input type=\"text\" name=\"comment\"> <input type=\"submit\" value=\"Add member\"></form><br><br>";

  my $usr = $query->param("userperson");
  my $parent = $query->param("parentperson");

  return $str unless($usr);

  my $userref = getNode($usr, "user");
  $usr = encodeHTML($usr);
  return $str."No such user ($usr)!" unless($userref);
  my $parentref;
  if($parent)
  {
      $parentref = getNode($parent, "user");
      $parent = encodeHTML($parent);
      return $str."No such user ($parent)!" unless($parentref);
  }

  $$parentref{node_id} ||= 0;

  return "Already has a parent!" if $DB->sqlSelect("familytree_id", "familytree", "familytree_user=$$userref{node_id}");

  my $cmt = $query->param('comment');
  $DB->sqlInsert("familytree", {'familytree_user' => $$userref{node_id}, 'familytree_parent'=>$$parentref{node_id}, 'familytree_comment'=>$cmt});

  return $str."User ($usr) added!"
%]
<hr><br><br>
[%
  my $prt = int($query->param("childof"));
  $prt ||= 0;
  my $csr = $DB->sqlSelectMany("*", "familytree", "familytree_parent=$prt");

  my @children;
  while(my $ROW = $csr->fetchrow_hashref)
  {
    push @children, $$ROW{familytree_user};
  }

  my @parentarray;
  my $cur = $prt;
  my $parent = $cur;

  my $str="";

  do
  {
      #$str.="*p:$parent<br>c:$cur<br>";
      $cur = int $parent;
      $parent = $DB->sqlSelect("familytree_parent", "familytree", "familytree_user=$cur");
      push @parentarray, $cur; 
      #$str.="p:$parent<br>c:$cur<br>";
  } while($cur != $parent);

  foreach(reverse @parentarray)
  {
   my $num = $_;
   my $title;
   #$str.="n:$num<br>";
   $title = "&lt;ETHER&gt;" if($num==0);
   my $n = getNodeById($num);
   $title = $$n{title} unless($title);
   $str.=linkNode($NODE, $title, {'childof'=> $num})." =&gt; ";
  }

  $str.="<ul>";  
  foreach(@children)
  {
   my $num = $_;
   my $title;
   $title = "&lt;ETHER&gt;" if($num==0);
   my $n = getNodeById($num);
   $title = $$n{title} unless($title);

   my $cmt; $cmt = "(".encodeHTML($DB->sqlSelect("familytree_comment", "familytree", "familytree_user=$num")).")" unless($num == 0);
   $cmt = "" if ($cmt eq "()");
   $str.="<li>".linkNode($NODE, $title, {'childof'=> $num})." ".$cmt;
  }

  $str.="</ul> (/msg [WolfDaddy] to join the tree)";
  return $str;
%]
