node.doctext:
[%
my @wit = (
  "The most neglected writeups on Everything:",
  "Adopt a writeup today:",
  "Won't you help a vote deprived writeup?",
  "Straight from no-man's land:");

return $wit[int(rand(@wit))];
%]<br>
<ol>
[%
return "disabled for performance reasons..."
  unless(isGod($USER));

my $nodes = selectNodeWhere({reputation => 0}, getType("writeup"),
  "hits limit 50", 1);
my $str;

return "No nodes!" unless($nodes);

foreach my $node (@$nodes)
{
  # A bit faster than doing a getnode
  my $n = getNodeById($node, "light");

  $str .= "<li>" . linkNode($n, undef, {lastnode_id => 0});
}

return $str;
%]
</ol>
