[%
sub myLinkNode {
	my ($TESTNODE, $title, $PARAMS) = @_;
	#getRef $NODE;

my $str;
$str .= ref $TESTNODE . '<br />';	

	return if not ref $TESTNODE and $TESTNODE == -1;
	return unless $TESTNODE;
	unless ($title) {
		$TESTNODE = getNodeById($TESTNODE, 'light') unless ref $NODE;
		$title = encodeHTML($$TESTNODE{title});
	}
#	return unless ref $TESTNODE;	



	if ($TESTNODE == -1) {return "<a>$title</a>";}
	$title ||= encodeHTML($$TESTNODE{title});
	$$PARAMS{node_id} = getId $TESTNODE;
	my $tags = "";

	$$PARAMS{lastnode_id} = getId ($GNODE) unless exists $$PARAMS{lastnode_id};
	#any params that have a "-" preceding 
	#get added to the anchor tag rather than the URL
	foreach my $key (keys %$PARAMS) {

		next unless ($key =~ /^-/); 
		my $pr = substr $key, 1;
		$tags .= " $pr=\"$$PARAMS{$key}\""; 
		delete $$PARAMS{$key};
	}
	if ((keys(%$PARAMS) == 2 && exists $$PARAMS{lastnode_id}) or (keys(%$PARAMS) == 1)) {
		if ($$PARAMS{lastnode_id} == 0) {
			"<a onmouseup=\"document.cookie='lastnode_id=0; ; path=/'; 1;\" href=" . $APP->urlGenNoParams($NODE) . $tags . ">$title</a>";
		} else {
			"<a onmouseup=\"document.cookie='lastnode_id=".$$PARAMS{lastnode_id}."; ; path=/'; 1;\" href=" . $APP->urlGenNoParams($NODE) . $tags . ">$title</a>";
		}
	} else {
		"<a href=" . urlGen ($PARAMS) . $tags . ">$title</a>";
	}
}

return myLinkNode('pipe', 'link');
%]


