####################################################################
# sub to format msgtext
#
sub FormatMessageText
{
   my $msgText = shift;
   $msgText =~ s/\</&lt;/g;
   $msgText =~ s/\>/&gt;/g;
   $msgText =~ s/\[([^\]]*?)$/&#91;$1/;	#dangling [ fixer #]
   $msgText =~ s/\[\]/\]/g;
   return $msgText;
}

####################################################################
# sub to output sent messages
#
sub OutputSentMessages
{
   my ($rowCtr, $dupMsgCtr, $prevMessageId, $prevMsgText, $prevTStamp, $prevForUser, $prevForUsergroup, $prevUserTitle, $prevUsergroupTitle, $toList, $msgUsername) = @_;

   my $sentMsg;
   my $temp;
   if ($rowCtr % 2 == 0)
   {
      $sentMsg .= '<tr class="evenrow">';
   }
   else
   {
      $sentMsg .= '<tr class="oddrow">';
   }
   $sentMsg.= '<td>
      <div class="msgheader">
         <div class="msgtofrom">You said to ';
   if ($prevForUsergroup > 0)
   {
      $sentMsg .= linkNode($prevForUsergroup, $prevUsergroupTitle, {lastnode_id=>0});
   }
   else
   {
      $sentMsg .= $toList;
   }
   if ($dupMsgCtr > 1)
   {
      $sentMsg .= ' ('.$dupMsgCtr.')';
   }
   $sentMsg .= ':</div>
      <div class="msgdate">(';
   if ($prevForUsergroup > 0)
   {
      $temp = $prevUsergroupTitle;
      $temp =~ s/ /_/g;
      $sentMsg.= '<a href="JavaScript:void(0);" onclick="Reply(\''.$temp.'\');">Reply to '.$prevUsergroupTitle.'</a>';
   }
   else
   {
      $sentMsg.= '<a href="JavaScript:void(0);" onclick="Reply(\''.$msgUsername.'\');">Reply to '.$msgUsername.'</a>';
   }
   $sentMsg .= ') '.$prevTStamp.'</div>
      <div class="clear">&nbsp;</div>
      </div>
      <div class="msgtext">'.parseLinks($prevMsgText).'</div>
      </td>
      </tr>';
   return $sentMsg;
}

