[%
  return "";
  my $csr = $DB->sqlSelectMany("setting_id","setting","vars like '%customstyle%'");
  $csr->execute();

  my $count = 0;
  while(my $row = $csr->fetchrow_hashref)
  {
    $count++;
  }

  return "Users with customstyle: $count<br />";
%]
[%
  return "";
  return unless $USER->{title} eq "jaybonci";
  my $str = "<ul> Patch Count: ".$DB->sqlSelect("count(*)","node","type_nodetype=".getType("patch")->{node_id});
  my $patches = [];
  my $csr = $DB->sqlSelectMany("*", "node", "type_nodetype=".getType("patch")->{node_id}." LIMIT 25");
  $csr->execute();
  while(my $row = $csr->fetchrow_arrayref)
  {
    my $patch = getNodeById($row->[0]);
    $str .= "<li>".$patch->{title}." (node_id: $patch->{node_id}) nuked </li>";
    $DB->nukeNode($patch, -1);
  }

  $str .= "</ul>";
  $str .= qq|<script>
  setTimeout(function(){ document.location.reload()}, 5000);
  </script>|;
  return $str;
%]
[%
  my $str = "";
  my $count = 0;
  foreach my $type ("superdoc","restricted_superdoc","oppressor_superdoc")
  {
    $str .= "<h2>$type</h2>";
    my @nodes = $DB->getNodeWhere({type_nodetype => getNode($type,"nodetype")->{node_id}});

    foreach my $n (sort {$a->{title} cmp $b->{title}} @nodes)
    {
      my $fullnode = $DB->getNodeById($n->{node_id});
      next unless($fullnode->{doctext} =~ /\S/);
      $count++;    
      $str .= linkNodeTitle($n->{title})."<br>";
    }
  }

  return "To go: $count<br>$str<br><br>";
%]
