node.doctext:
[%
  return $ENV{HTTP_X_FORWARDED_FOR};
%]

[%
  my $str = "";
  my $count = 0;
  my @nodes = $DB->getNodeWhere({type_nodetype => getNode("superdoc","nodetype")->{node_id}});

  foreach my $n (sort {$a->{title} cmp $b->{title}} @nodes)
  {

    my $fullnode = $DB->getNodeById($n->{node_id});
    next unless($fullnode->{doctext} =~ /\S/);
    $count++;    
    $str .= linkNodeTitle($n->{title})."<br>";
  }

  return "To go: $count<br>$str<br>";

%]
