       my $field = $$_{Field};
       $insertref->{$field} = $$nodeproto{$field};
       $insertref->{$field} ||=  $$tomb{$field};
       $insertref->{$field} ||= 0;
       $insertref->{"$table"."_id"} = $nid;

       $DB->sqlDelete($table,"$table"."_id=$nid");
       $DB->sqlInsert($table, $insertref);
    }
    $str.= Data::Dumper->Dump([$insertref]);
  }
