node.doctext:
[%
my $str = '';


if($query->param('op') eq 'login' && getId($USER) != $HTMLVARS{guest_user}) {
	$str.= "Hey.  Glad you're back.  Would you like to go to your ".linkNode($USER, "home node").' or to '.linkNode($HTMLVARS{default_node}).'?<br />';
	$str .= '...or back to '.linkNode($query->param('lastnode_id')).'?<br />' if ($query->param('lastnode_id'));
	return $str;
} elsif ($query->param('op') eq 'login') { 
	$str .="Oops.  You must have the wrong login or password or something:\n<p>\n";
} elsif ($$USER{title} ne 'Guest User') {
	$str.="Hey, ".linkNode($USER)."...  this is where you log in:<p>\n";
}else {
	$str .="Welcome to $HTMLVARS{site_name}.  Authenticate yourself:\n<p>\n";
}
#jb says: security fixes
my $pass = $query->param("passwd");
$pass =~ s/./\*/g;
$query->param("passwd", $pass);

$str .= "<form method=\"POST\" action=\"".$ENV{SCRIPT_NAME}."\" id=\"loginsuperdoc\">".
	"<input type=\"hidden\" name=\"op\" value=\"login\" />".	
	$query->hidden("node_id", getId($NODE))."\n".
	$query->hidden("lastnode_id", $query->param("lastnode_id"))."\n".

	$query->textfield (-name => "user",
		-size => 20,
		-maxlength => 20) . "<br>" .
	$query->password_field(-name => "passwd",
		-size => 10,
		-maxlength => 10) ."<br>".
	$query->checkbox("expires", "", "+10y", "save me a permanent cookie, cowboy!"). 
	$query->submit("sexisgood", "submit") .
	$query->end_form;
	$str.="[What's my password?|Forgot your password or username?]";
        $str.="<p>Don't have an account? [Create a new user|Create one]!";
$str;
%]
