node.doctext:
[{openform}]
[%
my $str='';
if($query->param('headforthehills')) {
	delete $$VARS{customstyle};
	return "<p>Is that better? Perhaps you'd rather [style defacer|edit the style manually].</p>";
}

if(length($$VARS{preferred_theme})&&($$VARS{preferred_theme}!=1854183)) {
	$str.= "\n<p>You are currently using the ".linkNode($$VARS{preferred_theme}).". You'll need to ".linkNode($NODE,'switch to the Zen theme',{switchToZen=>'true'})." to use this page.</p>".$query->checkbox('switchToZen', 0, '1', "Switch to Zen.");
}
if($query->param('switchToZen')) {
	delete $$VARS{preferred_theme};
	getTheme();
	$str.= "Zen theme enabled."; 
}
if($query->param('skeletonize')) {
	$$VARS{userstyle} =1931418;
	$str.= "Skeleton theme switched on."; 
}

if (!$query->param('puree')) {
	 $str.= "<p>This is a nice place to recycle your ekw preferences into a fresh new custom stylesheet!";
	if (defined($$VARS{userstyle}) && $$VARS{userstyle}!=1931418){
		$str.="You should probably switch to [skeleton stylesheet] before doing this.</p>".
		$query->checkbox('skeletonize', 1, '1', "Switch to [skeleton stylesheet].");
	}
	if (defined($$VARS{customstyle}) && length($$VARS{customstyle})) {
		$str .= "<p><strong>WARNING!</strong> You already have a custom stylesheet. 
		Using the Nice Friendly ekw Replicator will OVERWRITE your existing style.</strong></p>";
	}

	$str .= "<p>" . $query->submit('puree', 'Fire up that delightful little replicator thingamy') . '</p>';
	return $str;
}

my $css = "

/* These are the basic colours and fonts */
body, div, p, th, td, li, dd {
	background-color: ". $$VARS{'ekw_bgcolor'} . ";
	font-size: " . $$VARS{'ekw_textfontsize'} . ";
	font-family: " . $$VARS{'ekw_textfont'} . ";
	color: " . $$VARS{'ekw_textcolor'} . ";
} 

/* These are headings */

h1, h2, h3, h4 {
	font-family: ". $$VARS{'ekw_headingfont'} . ";
}

/* links ... */
a:link {
	color: ". $$VARS{'ekw_linkcolor'} . ";	
}

a:visited {
	color: ". $$VARS{'ekw_vlinkcolor'} . ";
}

a:active, a:hover {
	color: ". $$VARS{'ekw_alinkcolor'} . ";
}

/* The title of the nodelet */

.nodelet h2 {
	color:  ". $$VARS{'ekw_logomaintext'} . ";
	background-color: ". $$VARS{'ekw_logobackground'} . ";
}

/* The nodelet border colour should be the same as the nodelet title background */
.nodelet {
	border-color: ". $$VARS{'ekw_logobackground'} . ";
	background-color: ". $$VARS{'ekw_bgcolor'} . ";
}

pre, tt {
	font-size: " . $$VARS{'ekw_monofontsize'} . ";
}

.oddrow, #homenodeheader dt  {
	background-color: " . $$VARS{'ekw_oddrowcolor'} . ";
}


/* Apply some ekw-ish styling to the New Writeups nodelet */

#writeup_list span.writeupmeta {
	background-color: ". $$VARS{'ekw_bgcolor'} . ";
}

#writeup_list li {
	padding: 3px;
}

#writeup_list span.author a {
	display: block;
	text-align: center;
	margin-bottom: 2px;
	background-color: " . $$VARS{'ekw_oddrowcolor'} . ";
}

#writeup_list span.type {
	text-align: right;
}

#writeup_list li a {
	display: inline;
	padding: 0;
	border: 0;
}

#writeup_list span.writeupmeta a:link {
	color: ". $$VARS{'ekw_linkcolor'} . ";
}

#writeup_list span.writeupmeta a:visited {
	color: ". $$VARS{'ekw_vlinkcolor'} . ";
}

#writeup_list span.writeupmeta a:active, a:hover {
	color: ". $$VARS{'ekw_alinkcolor'} . ";
}

";

$$VARS{customstyle} = $css;

$str = "<p>Your old ekw settings have been recycled into a shiny new custom style. You should be seeing it now. 
Perhaps you'd like to [style defacer|edit it] to make it perfect.</p>";
$str .= "<p style='background-color: white; color: black; font-size: 12px; font-family: sans-serif'>";
$str .= $query->submit('headforthehills', "My screen is all messed up!") . "</p>";
$str;
%]

[% $query->end_form %]
