node.doctext:
[%

  return 'Curiosity killed the cat, ya know.' if(!exists($HTMLVARS{'group_gods'}{$$USER{node_id}}) && !exists($HTMLVARS{'group_content editors'}{$$USER{node_id}}));

  my $str = 'Everything\'s 100 Highest Rep Writeups of This Month (visible only to staff members, but maybe we should change that?):';

#my $authorID=getNode('Oolong', 'user')->{node_id};
#my $parentNode=getNode('Moo', 'e2node')->{node_id};
#my $nodeLink=linkNode(getNodeWhere({author_user=>$authorID, parent_e2node=>$parentNode}, 'writeup'));
#if (length($nodeLink)){
#  $str.=$nodeLink; 
#}
#else {
#  $str.="Sorry, couldn't find it.";
#}
#return $str;
  my $csr = $dbh->prepare('SELECT node_id FROM node WHERE type_nodetype=117 AND createtime > (CURDATE() - INTERVAL 1 MONTH) ORDER BY reputation DESC LIMIT 100');

  #my $csr = $dbh->prepare('SELECT node_id FROM writeup, node WHERE writeup.writeup_id=node.node_id AND node.createtime > (CURDATE() - INTERVAL 1 MONTH) ORDER BY writeup.cooled DESC LIMIT 50');

 # my $csr = $dbh->prepare('SELECT node_id FROM writeup INNER JOIN node ON writeup.writeup_id=node.node_id WHERE node.createtime > (CURDATE() - INTERVAL 1 MONTH) ORDER BY writeup.cooled DESC LIMIT 50');
 # This version is slightly less simple. I wonder if there's any advantage?

  $csr->execute();

  $str .= '<br><br><table width="100%"><tr bgcolor="#CCCCCC"><th width="32">#</th><th width="48">Rep</th><th width="200">Writeup</th><th width="200">Author</th></tr>';

  my $count=0;

  while(my $row = $csr->fetchrow_hashref())
  {

      my $bestnode = getNodeById($$row{node_id});

      next unless($bestnode);

      my $bestparent = getNodeById($$bestnode{parent_e2node});
      my $bestuser = getNodeById($$bestnode{author_user});
      my $bestrep = $$bestnode{reputation};
      $count=$count+1;

     $str.='<tr><td>'.$count.'</td><td>'.$bestrep.'</td><td>'.linkNode($bestnode, $$bestnode{title}).' - '.linkNode($bestparent, 'full').' <b>'.$$bestnode{cooled}.'C!</b></td><td> by '.linkNode($bestuser, $$bestuser{title}).'</td></tr>';

  }

  $str .= '</table>';


  return $str;
%]
