node.doctext:
[%
my $csr=$DB->sqlSelectMany ("*", "podcast JOIN node ON podcast_id = node_id", "1", "ORDER BY podcast_id DESC LIMIT 100");

return "There are no podcast links yet." unless $csr->rows;
my $str="<ul>";

my $GROUP = getNode('podpeople','usergroup');
my $id = getId($USER);


while (my $pod = $csr->fetchrow_hashref) {

$str.="<li><h4><a href='$$pod{link}'>$$pod{title}</a></h4><p>$$pod{description}</p>";

$str.="<p>".linkNode($$pod{podcast_id},'podcast node')."</p>" if grep /^$id$/, @{ $$GROUP{group} };

$str.="</li>";

}

$str.="</ul>";


return $str unless grep /^$id$/, @{ $$GROUP{group} };

$str .= '
    <hr />
    <b>Create a new podcast:</b><br />
    <form method="post">
    <input type="hidden" name="type" value="podcast">
    <input type="text" size="50" maxlength="64" name="node">
    <input type="hidden" name="op" value="new">
    <input type="hidden" name="displaytype" value="edit">
    <input type="submit" value="create">
    </form>
';

return $str;

%]
