node.doctext:
[%
return 'Temporarily disabled following XP/level changes.';
return if ($$VARS{GPoptout});

return '<em>This is gonna <strong>hurt</strong> you, '.$$USER{title}.'.</em> No kidding: You\'ll regret it.<br />' if $$USER{GP}<100 and $$USER{GP}>9;
%]

[%
return;
return "It's gonna be pretty hard to punch thyself until you take [User Settings|the handcuffs] off." if ($$VARS{GPoptout});

return '<em>You want <strong>PAIN</strong>? You can\'t <strong>handle</strong> the pain, '.$$USER{title}.'!</em><br /><small>(Come back when you actually have GP to burn.)</small>' if $$USER{GP}<10;

if($query->param('punch') 
  and (not defined $$VARS{punched}
  or time - $$VARS{punched} > 720)) {
	return if getId($USER) == $HTMLVARS{guest_user};

        $DB->sqlInsert("seclog", {'seclog_node'=>$$NODE{node_id}, 'seclog_user' => $$USER{node_id}});

	$$USER{GP} -= 10;
	$$VARS{punched} = time;
	return '<p>Owwww.....</p><p>You just lost 10 [GP]!</p>';
} elsif ($query->param('punch')) {
	return 'that\'s enough for now.  Go heal.';
} 
linkNode($NODE, 'Go ahead. Hit me in the face.', { punch => 1 });
%]
