node.doctext:
[%

return "The battlefield is quiet now.";

use JSON;
my $str;
my $randVillain;
my $pillageStr;

my $enemyList = '';
if (htmlcode('isInUsergroup',$$USER{user_id}.',heroes')) {
	$enemyList = "villains";
}

if (htmlcode('isInUsergroup',$$USER{user_id}.',villains')) {
	$enemyList = "heroes";
}

return "You must [Halls of Valhalla|choose a side] before waging war...." unless $enemyList;

my @enemySet = @{getNode($enemyList,'usergroup')->{group}};
my @enemyList;
foreach(@enemySet) {
push @enemyList, getNodeById($_)->{title};
}

my $enemyStr = join(", ",@enemyList);

my $strikeStr = "<form method='post'>
<p>Enemy List:</p>

<p>$enemyStr</p>


Name Your Enemy (cost 1XP): <input type='text' name='chooseenemy' value='' /> (or leave blank to attack randomly for free!)<br />
<input type='hidden' name='node_id' value='".$$NODE{node_id}."' />
<input type='hidden' name='strike' value='1' />
<input type='submit' name='submit' value='Strike True!' />
</form>";




if (!$$VARS{thewar}) {
$$VARS{thewar} = '{"health": 300}';
}

my $scalar = from_json($$VARS{thewar});

if (time - $scalar->{laststrike} <= 5) {return "Thou art too exhausted from thy last strike to strike again. Please wait a moment ..." . $strikeStr;}

my @enemies = @{getNode($enemyList,'usergroup')->{group}};
my $arrcount = @enemies;
my $damage = 0;
$arrcount--;

if ($query->param("chooseenemy") gt '') {
	my $enemy = getNode($query->param("chooseenemy"),"user");
	if (!$enemy) {
		$str .= "<p>Thou art attacking a ghost ...</p>";
		return $str . $strikeStr;
	}
	my $enemyID = $$enemy{user_id};
	if (!htmlcode('isInUsergroup',$enemyID.",".$enemyList)) {
		$str .="<p>That is not an enemy!</p>";
		return $str . $strikeStr;
	}
	adjustExp($USER,-1);
	$randVillain = $enemy;
}
else {
	$randVillain = getNodeById($enemies[int(rand($arrcount))]);
}

return "All of your enemies have been slain! But surely more will come ..." unless $randVillain;

my $vVars =getVars($randVillain);
my $vWarVars = $vVars->{thewar};
if (!$vWarVars) {
	$vWarVars = '{ "health": 300}';
}
$vWarVars = from_json($vWarVars);

my $warStr = '';

if ($scalar->{fallen}) {
	return "Thou hast fallen in battle, and have entered [Valhalla]. To return to battle, you must once again return to the [Halls of Valhalla] and choose your destiny.";
}

if ($query->param("strike")) {
	$scalar->{strike} += 1;
	$scalar->{laststrike} = time;
	$$VARS{thewar} = to_json($scalar);
	setVars($USER,$VARS);
	my $bonus = 0;
	for my $key ( keys %{$scalar->{inventory}} ) {
		$bonus += $scalar->{inventory}->{$key}->{damageBonus};	
	}
	my $damage = int(rand(6))+getLevel($USER) + $bonus;
	$scalar->{damageDone} += $damage;
	$vWarVars->{health} -= $damage;
	if ($vWarVars->{health} <= 0) {
		$vWarVars->{fallen} = 1;
		$scalar->{kills} += 1;
		my $valhalla = getNode('valhalla','usergroup');
		if (!htmlcode('isInUsergroup',$$USER{user_id}.',valhalla')) {
			insertIntoNodegroup($valhalla,-1,$randVillain);
			updateNode($valhalla,-1);
		}
		removeFromNodegroup(getNode($enemyList,'usergroup'),$randVillain,-1);
		updateNode(getNode($enemyList,'usergroup'),-1);

		for my $key ( keys %{$vWarVars->{inventory}} ) {
			$scalar->{inventory}->{$key} = $vWarVars->{inventory}->{$key};
			$pillageStr .="Thou hast pillaged <b>$key</b> - <i>".$vWarVars->{inventory}->{$key}->{description}."</i> - from the dead!<br />";
		}
		$vWarVars->{inventory} = {};
	}

	$vVars->{thewar} = to_json($vWarVars);
	setVars($randVillain, $vVars);

	$warStr = to_json($scalar);
	$$VARS{thewar} = $warStr;
	my $wStr = from_json($$VARS{thewar});

	$str .= "Thy sword has rent <b>".$damage."</b> damage upon <b>".linkNode($randVillain)."</b>. ";
	$str .= $$randVillain{title}." <i>has fallen in battle!</i><br />$pillageStr" if ($vWarVars->{fallen});
	$str .= "Victory is at hand! Thy health is <b>".$wStr->{health}."</b>.";

}

return $str . $strikeStr;

%]
