node.doctext:
[%
my $str;
my $node = $query->param('num');

my $ref = $DB->sqlSelectHashref("*","node","node_id=$node");



if($DB->sqlDelete("nodegroup","node_id=$node"))
{
  $str .= "The item has been detached.  ";
}
else
{
  $str .= "Error detaching item.  ";
}

$str .= "Return to " . linkNode(getNodeById($query->param('lastnode_id')));
