node.doctext:
[%

my $querytext;

$querytext = 'EXPLAIN SELECT vote_id, count(vote_id) 
	AS totalvotes 
	FROM vote INNER JOIN writeup ON writeup_id=vote_id 
	WHERE NOT EXISTS (SELECT 1 
		FROM vote AS v2 
		WHERE v2.vote_id=vote.vote_id 
		AND voter_user=1503725) 
	AND  wrtype_writeuptype<>177599 
	GROUP BY vote_id HAVING totalvotes<=3 
LIMIT 300';

#$querytext = 'EXPLAIN SELECT reputation
#FROM node
#WHERE type_nodetype = 16
#	AND title like \'Ra%\'
#LIMIT 100'

my $querytextcopy = $querytext;
$querytextcopy =~ s/\[/&#91;/g;
$querytextcopy =~ s/\]/&#93;/g;
$querytextcopy =~ s/\</&lt;/g;
$querytextcopy =~ s/\>/&gt;/g;
$querytextcopy = "<pre>" . $querytextcopy . "</pre>";

my $testexplainoutput = "<ul>";
my $dbh = $DB->getDatabaseHandle();
#my $csr = $dbh->prepare(
#	"EXPLAIN SELECT reputation
#	FROM node
#	WHERE type_nodetype = 16
#		AND title like \'Ra%\'
#	LIMIT 100");
my $csr = $dbh->prepare($querytext);
$csr->execute();
while (my $s = $csr->fetchrow_hashref)
	{
	foreach my $keys (sort(keys(%{$s}))) 
		{ $testexplainoutput .= "\n\t<li>" . $keys . ": " . $s->{$keys} . "</li>"; }
	}

$testexplainoutput .= "</ul>";

return $testexplainoutput . $querytextcopy;

%]
