node.doctext:
[%
return '<p>You need to sign in or '
	.linkNode(getNode('Create a New User','superdoc'), 'register')
	.' to use this page.</p>
	<!-- ' if ($$USER{node_id} == $HTMLVARS{guest_user});
# Save the last updated time so we can timestamp external documents which are affected
#  by preferences for caching purposes.  Epoch is Unix time() seconds for easy comparison.
use DateTime;
if (defined $query->param('sexisgood')) {
	$$VARS{'preference_last_update_time'} = DateTime->now()->epoch()-60;
}
$HTMLVARS{pageheader} = '<!-- put at end -->'.htmlcode('settingsDocs');
htmlcode('openform',-id=>'pagebody');
%]

<h2 id="appearance">Look and feel</h2>
<fieldset><legend>Theme</legend>
[%
return if ($$USER{node_id} == $HTMLVARS{guest_user});

delete $$VARS{preferred_theme} if $$VARS{preferred_theme} == $HTMLVARS{default_theme} ;

if($query->param('chosentheme')) {
	my $theme = getNodeById($query->param('chosentheme'));
	my $theme_type = $$theme{type}{title} if $theme;
	if ($theme_type eq 'stylesheet' or $query->param('chosentheme') eq 'default' ){
		delete $$VARS{preferred_theme} ;
		delete $$VARS{userstyle} ;
		$$VARS{userstyle} = $$theme{node_id} if $theme ;
	} elsif ($theme_type eq 'theme' || $theme_type eq 'themesetting') {
		$$VARS{preferred_theme} = $$theme{node_id};
	}
	getTheme();
}
local $$THEME{default_style} = getVars(getNodeById($HTMLVARS{default_theme}))->{default_style} if $$VARS{preferred_theme};

my $sheets = getVars( getNode( 'theme menu' ,'setting' ) ) ;
foreach ( keys %$sheets ){
	if ( $$sheets{$_} >= $HTMLVARS{stylesheetFixLevel} ){
		my $sh = getNodeById($_) ;
		$$sheets{$_} = $$sh{title}.(
			$$sh{title} ne $$THEME{default_style} ?
			' by '.getNodeById($$sh{author_user})->{title} : ' (default)');
	}else{
		delete $$sheets{$_} ;
	}
}
$$sheets{ $$VARS{userstyle} } = getNodeById($$VARS{userstyle}) -> {title}
	unless !$$VARS{userstyle} || $$sheets{ $$VARS{userstyle} } ;

my @values = ( 'default' , keys %$sheets ) ;
$$sheets{default} = 'default zen theme' ;
$$sheets{ $$VARS{userstyle} } .= '*' if $$VARS{userstyle} ;
my $str = 'Choose a theme: ' . $query -> popup_menu( -name => 'chosentheme', values=> \@values ,
	labels=>$sheets , default=>$$VARS{userstyle}||'default') ;
if ( $$VARS{preferred_theme} ){
	$str .= htmlcode('typeMenu', 'chosentheme', $$VARS{preferred_theme}, 'theme', 'themesetting');
	$str =~ s"<option.*$HTMLVARS{default_theme}.*</option>"" ;
	$str =~ s/(<select.*>)/$1<optgroup label="Zen theme styles">/ ;
	$str =~ s'</select>.*<select.*>'</optgroup><optgroup label="Unsupported themes">' ;
	$str =~ s'</select>'</optgroup></select>' ;
}
"$str\n<br><small>If you choose 'default zen theme', your theme will change when the default is changed.
	If you choose '$$THEME{default_style}' it won't.
	There are more (possibly less well-maintained) themes at [Theme Nirvana].</small><br>" ;
%]
[{varcheckboxinverse:nogradlinks,Show the softlink color gradient}] [{varcheckboxinverse:nogradekw,using colors from my theme}]<br>
</fieldset>

<fieldset><legend>Quick functions</legend>
[%
return unless $$VARS{noquickvote};
htmlcode('varcheckboxinverse', 'noquickvote' , 'Enable quick functions (a.k.a. AJAX).') .
'<br><small>(Voting, cooling, chatting, etc will no longer require complete pageloads. Highly recommended.)</small>
<br>' ;
%]
<label>On-page transitions:[{varscombobox:fxDuration, 0,
	1,Off (instant),
	100,Supersonic,
	150,Faster,
	0,Fast (default),
	300,Less fast,
	400,Medium,
	600,Slow,
	800,Slower,
	1000,Glacial}]
</label>
<br>
[{varcheckboxinverse:noreplacevotebuttons,Replace }]
	<label><input type="radio" name="sampledummy">+</label><label><input type="radio" name="sampledummy">-</label>
	voting buttons with <input type="button" value="Up"><input type="button" value="Down"> buttons.
<br>
[{varcheckbox:votesafety,Ask for confirmation when voting.}]
<br>
[{varcheckbox:coolsafety,Ask for confirmation when cooling writeups.}]<br>
</fieldset>

<h2 id="writeups">Your writeups</h2>

<fieldset><legend>Editing</legend>
[{varcheckbox:HideWriteupOnE2node,Only show your writeup edit box text on the writeup's own page}]
	(useful for slow connections; [E2 Options: Don't default to writeup edit on e2nodes|more information])<br>
[{varcheckbox:settings_useTinyMCE,Use WYSIWYG content editor to format writeups}]<br>
Writeup edit box display size: [{varsComboBox:textareaSize,0, 0,20 x 60 (Small) (Default), 1,30 x 80 (Medium), 2,50 x 95 (Large)}]
([E2 Options: Editbox size choices|more information])<br>
</fieldset>
<fieldset><legend>Writeup Hints</legend>
Check for some common mistakes made in creating or editing writeups.<br>
[{varcheckboxinverse:nohints,Show critical writeup hints}] (recommended: on)<br>
[{varcheckboxinverse:nohintSpelling,Check for common misspellings}] (recommended: on)<br>
[{varcheckboxinverse:nohintHTML,Show HTML hints}] (recommended: on)<br>
[{varcheckbox:hintXHTML,Show strict HTML hints}]<br>
[{varcheckbox:hintSilly,Show silly hints}]
</fieldset>

<h2 id="noders">Other users</h2>
<fieldset><legend>Other users' writeups</legend>
<label>Anonymous voting:[{varscombobox:anonymousvote , 0,
	0,Always show author's username ,
	1,Hide author completely until I have voted on a writeup ,
	2,Hide author's name until I have voted but still link to the author}]
</label>
</fieldset>
[%
return if (getId($USER) == $HTMLVARS{guest_user});
my $favoritelinktype = getId(getNode("favorite","linktype"));

my $csr = $DB -> sqlSelectMany("*","links", "from_node = $$USER{'node_id'} AND
                                          linktype  = $favoritelinktype" );

my @list;
while( my $favnoder = $csr -> fetchrow_hashref){
   $favnoder = getNodeById($$favnoder{'to_node'});
   push @list, '<li>'.$query->checkbox('cutlinkto_'.$$favnoder{'node_id'},'','1','').linkNode($favnoder).'</li>';
}

my $str ;
if(@list){
	$str .= '<fieldset><legend>Favorite other users</legend>Your favourite noders are:<ul>' .
	join("\n",@list) .
	'</ul>
	<input type="hidden" name="op" value="linktrim">' .
	htmlcode('verifyRequestForm', 'linktrim') .
	$query->hidden("cutlinkfrom",$$USER{'node_id'}) .
	$query->hidden("linktype",$favoritelinktype) .
	"Check a user's name to remove them from the list.</fieldset>";
}

$str .= '<fieldset><legend>Less favorite other users</legend>';

if(my $uname = $query->param('nomail')) {
	htmlcode('ignoreUser',"$uname");
}

foreach ($query->param) {
	next unless /restore_(\d+)/;
	my $restore = $1;
	$DB->sqlDelete('messageignore', "messageignore_id=$$USER{node_id} and ignore_node=$restore");
}

$str.='Block messages from: '
   .$query->textfield(-name=>'nomail', default=>'', override=>'1')
   .'<br><small>(If you enter a user name here, you will not receive private messages from this person or see their
   comments in the Chatterbox. If you enter a group, you will not receive messages to that group.)</small><br>';

$csr = $DB->sqlSelectMany('ignore_node', 'messageignore', 'messageignore_id='.$$USER{node_id});
@list = ();
while (my ($u) = $csr->fetchrow) {
	push @list, '<li>'.$query->checkbox('restore_'.$u, '', '1', '').linkNode($u).'</li>';
}
$csr->finish;
$str.= '<br>You are ignoring:<ul>'.join ("\n",@list)."</ul>
Check a user's name to remove them from the list.<br>
<small>More thorough ignoring is available at the [Pit of Abomination].</small><br>" if @list;

$str .= '<br>If one of your messages is blocked, you will be informed:'.
htmlcode('varsComboBox','informmsgignore','0', '0','by private message','1','in the chatterbox',2,'both ways',3,'do not inform (bad idea)').
'<br><small>(<strong>Warning</strong>: "do not inform" could lead to you engaging in a one-sided conversation without noticing.)</small>
</p>
</fieldset>';

$str.'<br>';
%]
<script type="text/javascript" src="/node/jscript/settings+script"></script>
[{closeform:Save Settings}]
[%
return ' -->' if $$USER{node_id} == $HTMLVARS{guest_user};
%]
