node.doctext:
[%
my $UID = $$USER{node_id};

return "Shoo, this is only meant for [zenmasters<usergroup>]."
  unless htmlcode('isinUsergroup',"$UID,zenmasters");

my $str;

$str .= "<p>Welcome to the stylesheet search. Gimme a string, and I'll
tell you which stylesheets have it in them.</p>\n";

$str .= htmlcode('openform');

$str .= $query -> textfield({-name => 'zensearch',
                             -id => 'zensearch'});

$str .= htmlcode('closeform');

my $zensearch = $query -> param('zensearch');

if($zensearch){
  my $type_id = getNode("stylesheet","nodetype") -> {node_id};

  my $sql = "SELECT node_id FROM node JOIN document ON node_id=document_id
             WHERE type_nodetype=$type_id AND doctext LIKE ?";

  my $csr = $dbh -> prepare($sql);
  $csr -> execute("%$zensearch%");
  my @sheet_ids;

  while(my $row = $csr -> fetchrow_hashref){
    push @sheet_ids, $row -> {node_id};
  }
  $csr -> finish;

  if(@sheet_ids){
    $str .= "<p>These are the stylesheets that have '$zensearch' in them:</p>\n";
    $str .= "<ul class=\"linklist\">\n";
    foreach my $sheet_id(@sheet_ids){
      $str .= "<li>".linkNode($sheet_id)."</li>\n";
    }
    $str .= "</ul>\n";
  }
  else{
    $str .= "<p>Looks like no stylesheets have the '$zensearch' string
                in them</p>\n";
  }
}

return $str;
%]
