[%
return '' if ( ! isGod( $USER ) );

my $param_row = sub {
    # $display_name     Name shown to user
    # $param_name       Name of form field
    # $user_field       Name of user setting in $$VARS
    # $theme_field      Name of field in $$THEME
    # $noswatch         Don't show a swatch link.
    # $maxlength         Max value length
    my ( $set, $display_name, $param_name, $user_field, $theme_field, $noswatch, $maxlength ) = @_;

    $user_field = 'theme' . $theme_field;

    $maxlength ||= 18;

    my $val = $query->param( $param_name ) || $$VARS{ $user_field } || $$THEME{ $theme_field };
    my $str = '';

    $val =~ s/[^a-zA-Z0-9,#\- \:\;]//g;

    if ( $set ) {
        $$VARS{ $user_field } = $val;
        $$THEME{ $theme_field } = $val;
    }

    $query->param( $param_name, $val );

    $str .= "<tr valign=\"top\">\n<td align=\"right\">" . $display_name . "</td>\n";
    $str .= "<td>" . $query->textfield( $param_name, $val, 12, $maxlength );
    if ( $theme_field ) {
        unless ( $noswatch ) {
            $str .= " (<a alt=\"Show " . $display_name . " color swatch\" ";
            $str .= "href=\"javascript:pop_small( '/index.pl?node=color_swatch&bgcolor=' + escape( ";
            $str .= "document.forms.ekwprefs." . $param_name . ".value ), 'color_swatch' );\">swatch</a>)\n";
        }
        $str .= "</td>\n";
        $str .= "<td>" . $$THEME{ $theme_field };
        $str .= " (<a href=\"javascript:set_ekw_field( document.forms.ekwprefs." . $param_name . ", ";
        $str .= "'" . $$THEME{ $theme_field } . "' );\">restore</a>)</td>\n";
    } else {
        $str .= "<td>&nbsp;</td>";
    }
    $str .= "</tr>\n\n";

    return $str;
};

my $str = '';

$str .= '
<tr bgcolor="#dddddd">
<td class="oddrow"><b>Attribute</b></th>
<td class="oddrow"><b>Value</b></th>
<td class="oddrow"><b>Theme Default</b></th>
</tr>
';

my $set = ( $query->param( 'set_values' ) eq 'true' );

# Fields
$str .= &$param_row( $set, 'Background Color',     'bgcolor',    'ekw_bgcolor',    'bodyBgcolor' );
