node.doctext:
[%

if(defined($query->param('switchToZen'))) {
	delete $$VARS{preferred_theme};
	getTheme();
	return "Zen theme enabled."; 
}

if($$VARS{preferred_theme} && $$VARS{preferred_theme}!=1854183) {
  return "You might like to ".linkNode($NODE,'switch to the Zen theme',{switchToZen=>'true'})." since the others are no longer supported and subject to breakage.<br>";
}


%]
<p>The following is a list of E2 themes in order of current popularity (based on users logged in over the last month).</p>
[%
my $queryText;
my $rows;
my $dbrow;
my $str;
my %styles;
my $key;
my $n;

my $total=0;

if(defined($query->param('clearVandalism'))) {
	delete($$VARS{customstyle});
}

if(length($$VARS{preferred_theme})) {
	$str .= "\n<p>You are currently using the ".linkNode($$VARS{preferred_theme}).".</p>";
}


sub hashValueDescendingNum
{
   $styles{$b} <=> $styles{$a};
}

my $nodeId = getId($NODE);
my $uid = getId($USER)||0;
my $dbh = $DB->getDatabaseHandle();
return 'No database handle!' unless $dbh;

# only show themes for "active" users (in this case lastseen within 1 months
$queryText = "SELECT DATE_ADD(CURDATE(), INTERVAL -1 MONTH)";
$rows = $dbh->prepare($queryText)
   or return $rows->errstr;
$rows->execute()
   or return $rows->errstr;
my $cutoffDate = $rows->fetchrow_array();

$queryText = "SELECT setting.setting_id,setting.vars FROM setting,user WHERE setting.setting_id=user.user_id AND user.lasttime>='$cutoffDate' AND setting.vars LIKE '%preferred_theme=%'";
#$str .= '<p>'.$queryText.'</p>';

$rows = $dbh->prepare($queryText)
   or return $rows->errstr;
$rows->execute()
   or return $rows->errstr;

while($dbrow = $rows->fetchrow_arrayref)
{
   $$dbrow[1] =~ m/preferred_theme=([0-9]+)/;
   if (exists($styles{$1}))
   {
      $styles{$1} = $styles{$1}+1;
   }
   else
   {
      $styles{$1} = 1;
   }
}

$str .= '<table align="center">
   <tr>
   <th>Stylesheet Name</th>
   <th>Author</th>
   <th>Number of Users</th>
   </tr>';
my $ctr = 0;
foreach $key (sort hashValueDescendingNum (keys(%styles))) {
   $ctr++;
   $n = getNodeById($key+0);

   if ($ctr%2==0)
   {
      $str .= '<tr class="evenrow">';
   }
   else
   {
      $str .= '<tr class="oddrow">';
   }

   $str .= '<td>'.linkNode($n, '', {lastnode_id=>0}).'</td>
      <td style="text-align:center">'.linkNode($$n{author_user}, '', {lastnode_id=>0}).'</td>
      <td style="text-align:right">'.$styles{$key}.'</td>
      </tr>';
      $total+=$styles{$key};
}
$str .= "<tr><td colspan='2'>Total:</td><td style='text-align:right'>$total</td></tr>";
$str .= '</table>
<p style="text-align:right;font-size:75%">Feedback to [Oolong], though this is very closely based on [Theme Nirvana], and [in10se] made that.</p>';

return $str;
%]
