node.doctext:
[%
return 'Curiosity killed the cat, this means YOU '.linkNode($USER, $$USER{title}) unless(isGod($USER));
my $days = int $query->param('days');
$days = 2 if ($days < 1);
my @ignoreTypes = qw/writeup e2node draft user debatecomment/; 
my $ignoreList = join ', ', map { getType($_)->{node_id} } @ignoreTypes;
my $whereStr = <<SQLEND;
  createtime >= DATE_SUB(NOW(), INTERVAL $days DAY)
  AND type_nodetype NOT IN ($ignoreList)
  ORDER BY CREATETIME DESC
SQLEND
my $csr = $DB->sqlSelectMany("*", "node", $whereStr);

my $str = '<ul>';

while(my $row = $csr->fetchrow_hashref())
{
   my $typename = getNodeById($$row{type_nodetype});
   $str .= '<li>'.linkNode($row, $$row{title})." - ".$$typename{title};
}

$str .= '</ul>';

return $str;
%]
