node.doctext:
[%


local *fisher_yates_shuffle = sub {
    my $array = shift;
    my $i;
    for ($i = @$array; --$i; ) {
        my $j = int rand ($i+1);
        next if $i == $j;
        @$array[$i,$j] = @$array[$j,$i];
    }
};


my $text = $query->param("text");
my $numbreaks;
$numbreaks = $query->param("numbreaks");
$numbreaks ||= 0;
$numbreaks = int($numbreaks);


my $str;

if (not $text) {
  $str.="Type in something you'd like to see messed up:<br>";
} else {
  my @words = split " ", $text;
  while ($numbreaks--) {
     $words[rand(int(@words))].="\n";
  }
  fisher_yates_shuffle(\@words);  
  $text = join " ", @words;
  $query->param('text', $text);
}

$str.=htmlcode('openform');
$str.="insert ".$query->textfield("numbreaks", "", 2, 2)." line breaks<br>";
$str.=$query->textarea("text", $text, 40, 60,"" , "wrap=virtual");
$str.=htmlcode('closeform');
$text =~ s/\n/\&lt\;br\&gt\;\<br\>/gs;
$text =~ s/\</\&lt\;/g;
$text =~ s/\>/\&gt\;/g;
$str.$text;

%]
