my $default ='';
my $lnid = getId($NODE);

$str.= $query->textfield(-name => 'node',
-id => 'node_search',
  -default => $default,
  -size => 28,
  -maxlength => 80);
my $types = getVars(getNode("default searchtypes", "setting"));
