node.doctext:
[%
return if $$USER{node_id} == $HTMLVARS{guest_user};

my $str = '<form method="post">
<input type="hidden" name="node_id" value="1411291" />';

my $inputname = '';

if($query->param('ns_user'))
{
	$inputname = $query->param('ns_user');
}
else
{
	$inputname = $$USER{title};
}

$str.="<b>Don't want your nodeshells searchable? You can turn it off in [User Settings].</b><br /><br />";

$str.= 'Look up user:<br /><input type="text" name="ns_user" 

value="'.encodeHTML($inputname).'">&nbsp;<input type="submit" value="View Nodeshells" /></form><hr /><br />';

my $nsUser = getNode($inputname, 'user');

return $str.'No such user "'.encodeHTML($inputname).'"<br />' unless $nsUser;


my $nsVars = getVars($nsUser);

return $str.linkNodeTitle($$nsUser{title}).'\'s nodeshells are not searchable.'
	if ( ($$nsVars{hideNodeshells}) && (!isGod( $USER )) && (!($$nsUser{title} eq $$USER{title})) );


my $csr = $DB->sqlSelectMany('title,
	(Select count(*) From nodegroup Where nodegroup_id = e2node_id Limit 1) AS groupcount'
	, 'e2node JOIN node ON e2node_id=node.node_id'
	, 'createdby_user='.$$nsUser{node_id}
	, 'HAVING groupcount = 0');

my @nodes;
while(my $row = $csr->fetchrow_hashref) {
	push @nodes, $$row{title};
}


$str.= '<p>(Be sure to check out [your filled nodeshells], too.)</p> <p><strong>'
	.scalar(@nodes).'</strong> nodeshells created by '
	.( ($$nsUser{title} eq $$USER{title} ) ? "you" : $$nsUser{title} )
	.' which do not contain writeups:</p>
<ul>
';

foreach(sort {lc($a) cmp lc($b)} @nodes) {
	$str.='<li>'.linkNodeTitle($_).'</li>
';
}

$str.='</ul>';

return $str;
%]
