node.doctext:
[%
return if $$USER{node_id} == $HTMLVARS{guest_user};

my $str = '<form method="post">
<input type="hidden" name="node_id" value="'.$$NODE{node_id}.'" />';

my $inputname = '';

if($query->param('ns_user'))
{
	$inputname = $query->param('ns_user');
}
else
{
	$inputname = $$USER{title};
}

$str.="<b>Don't want your nodeshells searchable? You can turn it off in [Advanced Settings].</b><br /><br />";

$str.= 'Look up user:<br /><input type="text" name="ns_user" value="'.encodeHTML($inputname).'">&nbsp;<input type="submit" value="View Nodeshells" /></form><hr /><br />';

my $nsUser = getNode($inputname, 'user');

return $str.'No such user "'.encodeHTML($inputname).'"<br />' unless $nsUser;


my $nsVars = getVars($nsUser);

return $str.linkNodeTitle($$nsUser{title}).'\'s nodeshells are not searchable.' if ( ($$nsVars{hideNodeshells}) && (!isGod( $USER )) && (!($$nsUser{title} eq $$USER{title})) );

my $nodetypeId = getId(getType('e2node'));
my $csr = $DB->sqlSelectMany(
  'node_id, title'
    . ', (SELECT COUNT(1)
           FROM nodegroup WHERE nodegroup_id = node.node_id)
       AS groupcount'
  , 'node JOIN e2node ON e2node.e2node_id = node.node_id'
  , "type_nodetype = $nodetypeId"
    . " AND createdby_user = $$nsUser{node_id}"
    . " HAVING groupcount=0"
  , "ORDER BY node.title"
  );

$str.= '<p>(Be sure to check out [your filled nodeshells], too.)</p> <p><strong>'.$csr->rows.'</strong> nodeshells created by '.( ($$nsUser{title} eq $$USER{title} ) ? "you" : $$nsUser{title} ).' which do not contain writeups:</p>
<ul>
';


my $icsr = $DB->getDatabaseHandle()->prepare('SELECT to_node FROM links WHERE linktype=1150375 AND from_node=?');

my $ctr;
my @nodes;
while(my $row = $csr->fetchrow_hashref) {
  $str .= '<li>' . linkNode($$row{node_id});
  if($icsr->execute($$row{node_id}))
  {
    if($icsr->rows>0)
    {
      $str .= ' - <b>Firm linked to:</b> ';
      $ctr = 0;
      while(my $irow = $icsr->fetchrow_hashref)
      {
        if ($ctr > 0)
        {
          $str .= ', ';
        }
        $str .= linkNode($$irow{to_node});
        $ctr++;
      }
    }
  }
}

$str.='</ul>';

return $str;
%]
