node.doctext:
[%
####
#### T  H  I  S     I  S     N  O  T     Y  O  U  R     S  C  R  A  T  C  H     P  A  D.
####
####                                        DO NOT DELETE THIS.
####
####
return;
%]

<script type='text/javascript'>

var deleteMe = 0;

function deletePushed() {
	deleteMe=1;
}

function confirmSubmit() {
	if (deleteMe) {
		var deleteYes = confirm('Are you sure want to delete this scratch pad?');
		if (deleteYes) {
			document.getElementById('skratch_form').deleteIt.value=1;
			return true;
		}
		else {
			return deleteMe=false;
		}
	}
	else {return true;}
}
</script>


[%
#/
#Function to check if user is allowed to be collab on given scratch
#pad.
my $shiftToDrafts=1; # flag to nerf functions for move to drafts system

local *canCollab = sub{
  my $collab_csv = shift;
  my %collab_ids;
  my @collab_names = split ",",$collab_csv;

  foreach my $collab_name (@collab_names) {

    $collab_name =~ s/^\s*//;
    my $N = getNode($collab_name,"user") ||
            getNode($collab_name,"usergroup");

    #I think I'm digging this autoviv stuff too much
    #(man perlglossary) --[Swap]
    if ($N) {
      \@collab_ids{
                   map $_->{node_id},
                   @{Everything::selectNodegroupFlat($N)}
                   };
    }

  }
  return exists $collab_ids{$$USER{node_id}};
};



my $usertitle = 'None';

##################################################
#INITIALIZATION

my $UID = $$USER{node_id};
my $isRoot = exists $HTMLVARS{'group_gods'}{$UID};
my $isCE = $isRoot || exists $HTMLVARS{'group_content editors'}{$UID};

#Since this variable is needed by both Perl and SQL below, it
#must be set to 0. Just undefining it isn't enough, since SQL will
#fail.
$isCE ||=0;

my $isPrintableView = $query->param('displaytype') eq 'printable';


###############
#return if isGuest

return "<p>You must be logged in to view scratch pads.</p>"
  if getId($USER) == $HTMLVARS{guest_user};

####################
#declaring variables

my ($str, $scratchTitle, $scratchText);

my ($inputname, $scratchID);

my $scratchUser = $$USER{node_id};
my $scratchTable='scratch2';
my $defaultPad = 0;
my $filterPrivate =0;
my $makePrivate = int $query->param("make_private");

$scratchTitle = CGI::unescape(
                              CGI::unescape($query ->
                                            param('scratch_title')
                                            )
                              );

my $queryname = $query->param('other_user') || $$USER{title};
$inputname = encodeHTML($queryname);

#Get the scratchpad ID by nodername and scratch title
if($scratchTitle and $inputname){
  my $noder = getNode($inputname,"user");
  if($noder){
    if(lc($scratchTitle) =~ /^\s*default\s*scratch\s*pad\s*$/){
      $scratchID = $$noder{user_id};
    }
    else {
      my $csr = $dbh -> prepare("SELECT scratch_id
                                 FROM scratch2
                                 WHERE scratch_user=$$noder{user_id}
                                       AND scratch_title= ?");
      $csr -> execute($scratchTitle);
      $scratchID = $csr -> fetchrow;
      if ($shiftToDrafts && !$scratchID){
		$HEADER_PARAMS{-status} = 303;
		$HEADER_PARAMS{-location} = "/user/$queryname/writeups/$scratchTitle";
		return;
      }
    }
  }
}

$scratchID = int $query->param('scratch_id') unless $scratchID;

my $formaction = ($shiftToDrafts ? getNode('Drafts', 'superdoc') : $NODE);
my $otherUserForm = "<p>You can look for another user's scratchpads and/or drafts here:</p>"
	if $shiftToDrafts;
$otherUserForm	.= '
 <form method="get" action='.urlGenNoParams($formaction).'>
 <input type="hidden" name="node_id" value="'.$$formaction{node_id}.'">
	Look up user: <br/>
	<input type="text" name="other_user" value="'.$inputname.'" /><br />
	<input type="submit" value="View scratch pads/drafts">
	</form>';

#############
#if no default scratch pad, create one

unless ($shiftToDrafts || $DB->sqlSelect('scratch_id', 'scratch',
                      "scratch_id = $$USER{node_id}")) {

	$DB->sqlInsert('scratch', {
                             'scratch_user'=> $$USER{user_id},
                             'scratch_id'=> $$USER{user_id},
                             'scratch_title'=>'',
                             'scratch_private' => 0,
                             'scratch_text' => ''});
}

# if empty default pad, remove it
$DB->sqlDelete('scratch',
	"scratch_id = $$USER{node_id}
		AND scratch_text = ''") if $shiftToDrafts;

###############################################
#PROCESS SUBMISSIONS

###########
#update

my $duplicateTitle;
my $newTitle;
my $currTitle;

if ($query->param('skratchsubmit')) {
  if ($scratchID==$scratchUser) {
    $$VARS{skratchShare} = !($makePrivate);
    $scratchTable='scratch';
    $DB->sqlUpdate($scratchTable,
                   {'scratch_user' => $$USER{user_id} },
                   "scratch_id=$scratchID");
  }
	$usertitle = $dbh->quote($$USER{title});

  my $updateWhereStr =  "scratch_id = $scratchID
                         AND
                         ((scratch_user = $$USER{user_id})
                         OR
                         (scratch_collab like $usertitle) OR $isCE)";

	$DB->sqlUpdate($scratchTable,
                 {'scratch_text' => $query->param("skratch_doctext"),
                  'scratch_private' => $makePrivate,
                  scratch_collab => $query->param("collab")} ,
                 $updateWhereStr) ;

  #Check for duplicate titles...
  $newTitle = $query->param("skratch_title");

  #In prepare statements, ? gets replaced with params during execute()
  #and everything gets escaped nicely. --Swap
  my $csr = $dbh -> prepare("SELECT scratch_title
                             FROM scratch2
                             WHERE scratch_user=$$USER{user_id}
                                 AND scratch_title= ?
                                 AND scratch_id <> $scratchID");
                                                    #^^^^^^
                                                    #We're looking for
                                                    #not this pad with
                                                    #the same title.
  $csr -> execute($newTitle);
  $currTitle = $csr -> fetchrow;

  if(lc($newTitle) eq lc($currTitle)){
    $duplicateTitle = 1;
  }
  else{
    $DB -> sqlUpdate($scratchTable,
                     {scratch_title => $newTitle},
                     $updateWhereStr);
  }
}

##############
#delete

if ($query->param('deleteIt')) {
 	$DB->sqlDelete("scratch2",
                 "scratch_id = $scratchID AND scratch_user = $$USER{user_id}");
	$scratchID=0;
}

#############
#insert

if ($query->param('new_scratch_title')) {
	my $newPrivate = $query->param('new_scratch_private') || 0;

  #Check for duplicate titles...
  $newTitle = $query->param("new_scratch_title");

  my $csr = $dbh -> prepare("SELECT scratch_title
                             FROM scratch2
                             WHERE scratch_title=?
                                   AND scratch_user = $$USER{user_id}");
  $csr -> execute($newTitle);

  $currTitle = $csr ->fetchrow;

  if(lc($newTitle) eq lc($currTitle)){
    $duplicateTitle = 1;
  }
  else{
    $DB->sqlInsert($scratchTable,
                   {
                    'scratch_user'=>$$USER{user_id},
                    'scratch_title' => $query->param('new_scratch_title'),
                    'scratch_private' => $newPrivate,
                    'scratch_text' => ''});

    $scratchID = $DB->sqlSelect('max(scratch_id)',
                                $scratchTable,
                                'scratch_user ='.$$USER{user_id});
  }

}

#Sanitise...
$newTitle = encodeHTML($newTitle);

######################
#set user to other user

if ($query->param('other_user')) {
	my $otherUser = getNode($query->param('other_user'), 'user');
	if ($otherUser) {             #other user actually exists
		$scratchUser = $$otherUser{node_id};
		if ($scratchID==$scratchUser) {
			$scratchTable='scratch';
			$defaultPad = 1;
		}
		$filterPrivate=1  unless ($otherUser == $USER);
	}
	else {
		## implied - scratchUser remains actual user, not "other user"
		$str .= "<p>No such user <b>";
    $str .= htmlScreen($query->param('other_user'))."</b></p>";
	}
}

my $userVars = getVars(getNodeById($scratchUser));
my $shareDefault = ($$userVars{skratchShare}==1);

##########################################################
#DISPLAYING INFO

if (!$scratchID) {

  #################################
  #SHOW PAD LIST
	my $converterator = htmlcode('convertscratchpads').'<br>' if $scratchUser == $$USER{node_id} and $shiftToDrafts;

	my $padList;

  my $scratchPads = $DB->sqlSelectMany(
     "scratch_id, scratch_private, scratch_collab, scratch_title",
     "scratch2",
     "scratch_user = $scratchUser
     ORDER BY scratch_title");

  my $defaultPadVars =
    $DB->sqlSelectHashref("scratch_id, scratch_private"
                         , 'scratch', "scratch_id = $scratchUser");

  $shareDefault = 0 if $$defaultPadVars{scratch_private};

  if ($$defaultPadVars{scratch_id}
      && ($shareDefault || $scratchUser == $$USER{node_id})
      ) {
    $padList.= "\n\t";
    $padList.= "<li>" if $shareDefault;
    $padList.= "<b>";
    $padList.= linkNode($NODE,'Default Scratch Pad',
                        {scratch_id => $scratchUser,
                         other_user => getNodeById($scratchUser)->{title} });
    $padList .= "</b>";
  }

	while ( my $s = $scratchPads->fetchrow_hashref) {
		my $isCollab = canCollab($$s{scratch_collab});

    # filterPrivate is just a proxy for "is this the actual user's list,
    # or someone else's?"
		if (!( ($filterPrivate) && ($$s{scratch_private}) && (!$isCollab) )) {
      my $scratchTitle=$$s{scratch_title};
      $scratchTitle = encodeHTML($scratchTitle);
      $scratchTitle='Nameless Pad' if (!$$s{scratch_title});

      # prevents private pads from having numbers
      # this is so private pads don't throw off the list when pointing
      # other users to one of your pads.

 			if ($$s{scratch_private}) { 
				$padList .="\n\t<br />";
			}
      else {
				$padList .= "\n\t</li><li>";
			}
			$padList.=linkNode($NODE,
                         $scratchTitle,
                         {scratch_id => $$s{scratch_id} });
      $padList .= ( $isCollab ? " <b>collab</b>" : "");
		}

	}
	$padList .= "\n\t</li>" if $padList;

	unless ($padList) {
		$converterator = '' unless $query -> param('converttodrafts');
		if ($filterPrivate) {
			$padList.="\n\t<li>This user has no public scratch pads.</li>";
		}
		else {
			$padList.="\n\t<li>You currently only have the Default Scratch
		                     Pad. You can create more using the text box
                         below.</li>" unless $shiftToDrafts;
		}
	}

	$padList ="<ol>" . $padList. "
            </ol>
            <p>
            <small><i>NB: Only public pads are numbered,
            for ease of referring other users to them.</i></small>
            </p>" if $padList;

  ################
  #create new pad form

	my $newPadStr= $shiftToDrafts ?
	
	'<p>Scratch pads are obsolete. '.linkNode(getNode('Drafts', 'superdoc')).' have taken over their function.</p>'
	
	: htmlcode('openform2','new_scratch')."

	<p>
	Scratch Pad Title<br />
	<input type='text' name='new_scratch_title' /><br />
	<input type='checkbox' name='new_scratch_private' value='1' /> Private?<br />
	<input type='submit' value='Create Scratch Pad' />
	</p>
	</form>";

  if($duplicateTitle){
    $newPadStr .= "<p><em> You already have a scratch pad titled
    \"$newTitle\". Please choose a different title.</em></p>";
  }


  ###############
  #return pad List

	return $str . $padList . $newPadStr . $converterator . $otherUserForm;

}
else {

  #################################################
  #SHOW SCRATCH PAD

	my $padVars = $DB->sqlSelectHashref("*", $scratchTable,
                                      "scratch_id=$scratchID");

	my $scratchText = $$padVars{scratch_text};
	my $ownPad = (( $$padVars{scratch_user} == $$USER{user_id} ) |
                ( $scratchID == $$USER{user_id} ));
	my $isCollab = canCollab($$padVars{scratch_collab});

	my $skratchUser = getNodeById($$padVars{scratch_user});

	$$padVars{scratch_private} = 1 if ($defaultPad && !$shareDefault);

	my $editableFormStr;
	my $sendMessageForm;
	my $padText;
	my $padForm;

  ###############
  #return if private

	return "<p>This scratch pad is private.</p>"
    if (!$ownPad && !$isCollab && $$padVars{scratch_private});

	my $padTitle = encodeHTML($$padVars{scratch_title});

	if ($ownPad || $isCollab || $isCE) {

    #################
    #open form for editing

    my $scratch_url = "/user/"
                      .rewriteCleanEscape($$USER{title})
                      ."/scratchpads/"
                      .rewriteCleanEscape($padTitle);

		$editableFormStr .= $query->start_form(
                                           -method => 'POST'
                                           , -action => $scratch_url
                                           , -name => 'skratch_form'
                                           , -id => 'skratch_form'
                                           , -onSubmit => 'return confirmSubmit();'
                                           );

		if ($ownPad) {

      #################
      #offer up chance to make private
      #and add collaborators
      #and display writeup hints

			$editableFormStr .= "\n\n<p>This is a private area (unless you
			say otherwise) for ". linkNode($USER,'you',{lastnode_id=>0}) . "
			to preview your writeups before submitting them. The HTML and
			text is displayed just like a regular writeup, except privately,
			viewable only by you and yours. You <strong>can</strong> choose
			to have other people be able to view it, so that you can get
			comments on a writeup or what not, but this is not the default.
			Use it to test or preview HTML (all the tags work just the
			same), or just to keep random notes to yourself.</p>\n\n";

			$editableFormStr.= "\n\n";

      $editableFormStr.= ( !($$padVars{scratch_private}) ?
		'<p>This is currently viewable to logged in noders. To make it
		private again, ' : 'This is currently private. If you wish to
		allow logged in noders to view it, un') .'check the following
		checkbox, then press the Update! button. For more information, see
		' . linkNodeTitle('E2 Options: Sharing your scratch pad') . '.<br
		/>'." <p><input type='checkbox' name='make_private' ".(
		$$padVars{scratch_private} ? "checked='true'" : "")." value='1' />
		Private?</p>";

			$editableFormStr .= " <p><b>Collaborators</b><br /> Enter user"
                         ." or usergroup names separated by commas."
                         ." Don't replace spaces by underscores."
                         ."<br/>"
                         .$query->textfield('collab',$$padVars{scratch_collab},80)."</p> ";

			$editableFormStr .= htmlcode('varcheckboxinverse',
                                   'noScratchHints,Show writeup hints here');

			unless ( $VARS->{noScratchHints} ) {
				$HTMLVARS{writeuphints_text} = $$padVars{scratch_text};
				my $hintText = htmlcode('writeuphints',1);

        if (defined $hintText && length($hintText) ) {
          $editableFormStr.= "\n".'<p>'.$hintText.'</p>';
        }
			}
		}
	}
	if (! $ownPad) {

    #################
    #display send message box

    $sendMessageForm =
    '<form method="post">
		<input type="hidden" name="skratch_user"
           value="'. $$skratchUser{title} .'" />

		<input type="hidden" name="other_user" value="'.$inputname.'" />
		<input type="hidden" name="scratch_id" value="'.$scratchID.'" />';
    $sendMessageForm .= "\n\t\t";

    $sendMessageForm .=  htmlcode('msgField',
                                  "skratchMsg, ,".
                                  $$NODE{node_id}.
                                  ",  ".
                                  $$skratchUser{node_id}. ","  .
                                  $$skratchUser{node_id});
    $sendMessageForm .=
      "\n\t\t".
        "<input type='hidden' name='scratch_title' value='$padTitle' />
		     <input type='hidden' name='node_id' value='$$NODE{node_id}' />
	       <input type='submit' name='message send' value='Send Message'/>
        </form>
         <hr>";
	}

  ##################
  #MathML

  my $TAGS = getVars(getNode('approved html tags','setting'));

	my $testOutOtherTags = 0;
	my $MATHTAGS = undef;
	my $mathStr;
	if ( (exists $HTMLVARS{group_gods}{$$USER{node_id}}) ||
       ($$USER{node_id}eq '1413262') ) {
		$testOutOtherTags = 1;
		$MATHTAGS = getVars(getNode('approved MathML tags','setting'));
		$HTMLVARS{'approvedTagsListRef'} = [$TAGS, $MATHTAGS];
		htmlcode('mergedApprovedTags','');
		my $combined = $HTMLVARS{'approvedTags'};
		if (ref($combined) eq 'HASH') {
			#just in case the htmlcode messes up, we don't want to
			#prevent admins from using ANY tags
			$TAGS = $combined;
		}
	}
	if ($testOutOtherTags) {

		$mathStr = '<p><big><strong>Note</strong></big>: Since you are an
	admin, MathML is also enabled for you for testing, but only on your
	own scratch pad. Viewing another scratch pad will act as normal -
	MathML tags will be stripped out.<br />Added tags: ';

		my @l;
		foreach (sort(keys(%$MATHTAGS))) {
			if ($MATHTAGS->{$_} eq '1') {
				push(@l, $_);
			}
      else {
				push(@l, $_.' ('.$MATHTAGS->{$_}.')');
			}
		}
		$mathStr .= join(', ', @l) . ' .</p>';
	}


  ####################
  #show actual pad text

  #Escape tags in title
   $padText = "<h2 class=\"title\">".linkNodeTitle($padTitle)."</h2>";
   if (!$isPrintableView) {
      $padText .= ""
            . "<p>(".linkNode($NODE,
                 'return to list of all pads',
                 {other_user=>$$skratchUser{title}}).")</p>"
            . "<p><abbr title=\"Copy and paste this text into a message to send a link to this scratch pad\">"
            . "<span class='scratchpadlink'>&#91;".$padTitle
            . "&#91;scratch by "
            . $$skratchUser{title}."&#93;|LinkToMe&#93;</span></abbr></p>"
            ;
   }



	if ($scratchText) {
		$padText .= '
		<div class="content">' .
        breakTags(parseLinks(screenTable(htmlScreen($scratchText, 
                                                    $TAGS),$NODE))) .
          '</div>
		<p><small>(' . length($scratchText) . ' characters total)</small></p>';
		if (length($scratchText) > 65000) {

			$padText .= '<p style="font-size:
			125%"><strong>Caution!</strong> Your scratch pad is longer than
			the maximum node length of 65,000 characters.  If you post it in
			its current form it will be truncated.</p>';

		}
	}
	else {
		$padText .= '<em>blank</em>';
	}

  #################
  #suggest links

  #if ( !$query->param("nosuggest") && ($ownPad || $isCollab || $isCE) ) {
  if (0) { # disable link suggester until the disable user setting is added

    $padText .= "<h4>Suggested Hardlinks</h4>";

    my $suggestStr = $scratchText;
    $suggestStr = htmlScreen($suggestStr);

    $suggestStr =~ s/,//g;
    $suggestStr =~ s/\.//g;
    $suggestStr = lc($suggestStr);


    my @wordlist = split(" ",$suggestStr);
    my $wordcount = @wordlist;

    my $tested;

    my @finallist;

    my $e2nodeType = getNode('e2node','nodetype')->{node_id};
    my $webby = getNode('Webster 1913','user')->{node_id};

    my $count = 0;
    my $finalStr;
    my $derivedStr;
    foreach (@wordlist) {
      $derivedStr = '';
      for (my $x = $count; $x < $wordcount; $x++) {
        if ($derivedStr eq '') {
          $derivedStr = $wordlist[$x] . " ";
          next if $DB->sqlSelect("1",
                                 "commonwords",
                                 "commonword = ".$dbh->quote($wordlist[$x]));
        }
        else {
          $derivedStr .= $wordlist[$x] . " ";
        }
        next if int($derivedStr) > 0;
        next if $$tested{$derivedStr};
        next if length($derivedStr) < 5;

        my $n = $DB->sqlSelect("node_id",
                               "node",
                               "title = ".$dbh->quote($derivedStr)."
                                AND
                                type_nodetype=$e2nodeType limit 1");

        $$tested{$derivedStr} = 1;
        if ($n) {
          my @derivedList = split(" ",$derivedStr);
          foreach (@derivedList) {
            $$tested{$_." "} = 1;
          }
          my $group = getNodeById($n)->{group};
          if ($group) {
            my @wuList = @{$group};
            next if ( scalar(@wuList) == 1  &&
                      getNodeById($wuList[0])->{author_user} == $webby  );
          }

          push @finallist, linkNode($n);
        }
        last if ($x - $count) > 5;
      }
      $count++;
    }
    $finalStr = join(", ",@finallist);


    $padText .= $finalStr;

  }


  if (!$isPrintableView) {
     $padText .= '<p align="right">'.linkNode($NODE,
                                              'link view',
                                              {displaytype => 'linkview',
                                               scratch_id => $scratchID})."</p>";
  }

	if ($ownPad || $isCollab || $isCE) {


		my (@WRTYPES) = getNodeWhere({type_nodetype =>
                                  getId(getType('writeuptype'))});

		my @WRTYPE;
		foreach (@WRTYPES) {
			push @WRTYPE, $_ unless ( lc($$_{title}) eq 'definition' ||
                                lc($$_{title}) eq 'lede');
		}

		my $chwriteup ="\n<select id='skratch_wutype' name='writeuptype'>";
		foreach (@WRTYPE) {
			$chwriteup.="\n\t<option value='".$$_{title}."'>".$$_{title}."</option>";
		}
		$chwriteup.="\n</select>";


		$padForm.="<p><b>Title</b> ";
    $padForm .=$query->textfield('skratch_title',
                                 $padTitle,80)."</p>";

    if($duplicateTitle){
      $padForm .= "<p><em>You already have a scratch pad titled
      <strong>\"$newTitle\"</strong>. Please choose a different title.
      The current title is <strong>$padTitle</strong></em></p>";
    }

		$padForm.="
			<input type='hidden' name='node_id' value='$$NODE{node_id}' /><br />
			<textarea id='skratch_doctext' name='skratch_doctext'"
			. htmlcode('customtextarea', '1')
			. " class='formattable' >"
			.  encodeHTML($scratchText)
			. '</textarea>

        <br />
        <input type="hidden" name="skratchsubmit" value="1" />
        <input type="hidden" name="scratch_id" value="'.$scratchID.'" />
        <input type="hidden" name="sexisgood" value="1" />
        <input type="hidden" name="deleteIt" value="" />

        <input type="submit" value="Update!" />'.
          "\n\n\t\t".

            ( $ownPad ? ($scratchID!=$$USER{node_id} ? '
        <input type="submit" onclick="deletePushed();" value="Delete!" />':'')
              :
              "
        <input type='hidden' name='make_private' ".
              ( $$padVars{scratch_private} ? "value='1'" : "value='0'")."  />

        <input type='hidden' name='collab' value='".
              $$padVars{scratch_collab}."'  /> "
             );
	}

   if (!$isPrintableView) {
      return $otherUserForm . $sendMessageForm. $editableFormStr .
             $mathStr . $padText . $padForm . '</form>';
   } else {
      return $padText;
   }

}

%]
