node.doctext:
[% 
my $foruser = $query->param('foruser');
my $str;
if ($foruser) {
   $str = htmlcode('userAtomFeed', $foruser);
   return $str if $str;
}

my $xml = getNode("Cool Archive Atom Source", "nodelet");
updateNodelet($xml);
return "Could not get feed!" unless $xml;
$str = $$xml{nltext};
utf8::encode($str);
$str;
%]
