node.doctext:
[%
    # We break if Guest User tries to ignore gods. Is this a Sign? In fact, we
    # break if Guest User clicks the button without the checkbox checked, too:
    #   Died at /usr/lib/perl5/5.005/Everything/HTML.pm line 1180.
    # Total collapse. So, hmmm... So don't do that! Ha ha.
    my $nogods = (($query->param("ebu_noadmins") == 1)?(1):(0));
    if ( $$USER{ 'node_id' } != $HTMLVARS{ 'guest_user' } ) {
        #jb says: I soo can't get this to work.
        #$$VARS{ebu_noadmins} = $nogods if $nogods;
        #$$VARS{ebu_noadmins} = 0 if($query->param("ebu_noadmins") == 0);
        #$$VARS{ebu_noadmins} = 1 if($query->param("ebu_noadmins") == 1);
        #$$VARS{ebu_noadmins} = 0 unless($$VARS{ebu_noadmins});
        }

my $skip = {
	'dbrown'=>1,
	'nate'=>1,
	'Webster 1913'=>1,
	'ShadowLost'=>1,
	'EDB'=>1
};

my $limit = 50;

$limit += (keys %$skip);
$limit += (keys %{ $HTMLVARS{'group_gods'}}) if($nogods == 1);

my $csr = $Everything::dbh->prepare('select node_id,title,experience,vars from user left join node on node_id=user_id left join setting on setting_id=user_id order by experience DESC limit '.$limit);

return 'Ack! Something\'s broken...' unless($csr->execute);

# Skip these users

my $uid = getId($USER) || 0;
my $node;
my $str = "<?xml version=\"1.0\" encoding=\"UTF-8\" standalone=\"yes\" ?> ";
my $step = 0;
my $lvlexp = getVars(getNode('level experience', 'setting'));
my $lvlttl = getVars(getNode('level titles', 'setting'));
my $lvl;
use Everything::Experience;

$str.="<EBU ebu_noadmins=\"$nogods\">";

while($node = $csr->fetchrow_hashref) {
  
	next if(exists $$skip{$$node{title}});
        next if(exists $HTMLVARS{'group_gods'}{$$node{node_id}} && $$VARS{ebu_noadmins});
        next if($step >= 50);

	$lvl = getLevel($node);
	my $V = getVars($node);

        $str.= "<bestuser>\n";
        $str.= " <experience>$$node{experience}</experience>";
        $str.= " <writeups>$$V{numwriteups}</writeups>\n"; 
        $str.= " <e2link node_id=\"$$node{node_id}\">$$node{title}</e2link>\n";
        $str.= " <level value=\"".getLevel($node)."\">".$$V{level}."</level>\n";
        $str.= "</bestuser>\n";

	++$step;
}

$str.="</EBU>";

$str;
%]
