node.doctext:
[%
use XML::Simple;

my $imported_patches_node = getNode("imported patch ids","setting");
my $imported_patches = getVars($imported_patches_node);

my $xmls = XML::Simple->new(
    RootName => undef,
    ForceArray => 1,
    ForceContent => 1,
    XMLDecl => 1,
    GroupTags => {patchset => "patch_id"},
);

my @id_array = keys %$imported_patches;

my $xml = $xmls -> XMLout({patchset => \@id_array});

return $xml; 
%]
