node.doctext:
[% 
my $foruser = $query->param('foruser');
$foruser =~ s/'/&#39;/g;
my $str;
if ($foruser) {
   $str = htmlcode('userAtomFeed', $foruser);
   return $str if $str;
}

my $xml = getNode("New Writeups Atom Source", "nodelet");
updateNodelet($xml);
return "Could not get feed!" unless $xml;
$str = $$xml{nltext};
utf8::encode($str);

'<?xml version="1.0" encoding="UTF-8"?>
<feed xmlns="http://www.w3.org/2005/Atom" xml:base="http://everything2.com/">
<title>Everything2 New Writeups</title>
<link rel="alternate" type="text/html" href="http://everything2.com/?node=25"/>
<link rel="self" type="application/atom+xml" href="?node=New%20Writeups%20Atom%20Feed&amp;type=ticker"/>
<id>http://everything2.com/?node=New%20Writeups%20Atom%20Feed</id>
' .
$str . '
</feed>' ;
%]
