node.doctext:
[% 
my $foruser = $query->param('foruser');
$foruser =~ s/'/&#39;/g;
my $str;
if ($foruser) {
   $str = htmlcode('userAtomFeed', $foruser);
   return $str if $str;
}

my $newwriteups = $DB->stashData("newwriteups");
$str = "<updated>";
my ($sec,$min,$hour,$mday,$mon,$year) = localtime;
$str .= sprintf("%04d-%02d-%02dT%02d:%02d:%02dZ", $year + 1900, $mon + 1, $mday, $hour, $min, $sec);
$str .= "</updated>\n";

my $numberofwriteups = 25;
my $node_ids = [];

while ($numberofwriteups and @$newwriteups) {
        my $N = shift(@$newwriteups);
        push @$node_ids, $N->{writeup_id};
        $numberofwriteups--;
}

$str .= htmlcode( 'atomiseNode' , $node_ids);

#utf8::encode($str);

return '<?xml version="1.0" encoding="UTF-8"?>
<feed xmlns="http://www.w3.org/2005/Atom" xml:base="http://everything2.com/">
<title>Everything2 New Writeups</title>
<link rel="alternate" type="text/html" href="http://everything2.com/?node=25"/>
<link rel="self" type="application/atom+xml" href="http://everything2.com/node/ticker/New+Writeups+Atom+Feed"/>
<id>http://everything2.com/?node=New%20Writeups%20Atom%20Feed</id>
' .
$str . '
</feed>' ;
%]
