node.doctext:
[%
use XML::Simple;
use LWP::Simple;

use Digest::MD5 qw(md5_hex);

my $xmlin = XML::Simple->new(
    RootName => undef,
    ForceArray => 1,
    XMLDecl => 1,
);

my $patchids_ref = $xmlin -> XMLin(get('http://'.($HTMLVARS{isDevServer} ? '' : 'dev1.' ).
	'everything2.com/node/ticker/imported+patches'));

my $import_status  = getId(getNode(
	($HTMLVARS{isDevServer} ? 'production-imported' : 'implemented'),'status'));

foreach my $patch_id( @{$patchids_ref -> {patch_id}}){
  my $imported_patch = getNodeById($patch_id);
  $imported_patch -> {cur_status} = $import_status;
  updateNode($imported_patch,-1);
}

my $production_status = getId(getNode(($HTMLVARS{isDevServer} ?
	'production-ready' : 'production-implemented' ),'status'));

my @patches =
  getNodeWhere({'cur_status'=>$production_status},'patch','createtime');

my @xmlPatches;
foreach my $patch (@patches){
  my %data;
  my $targetNode = getNodeById( $patch -> {'for_node'} );
  my $targetNodeType = getNodeById ( $targetNode -> {'type_nodetype'} );
  my $patchCode = $targetNode -> { $patch -> {'field'} };

  $data{'for_node'} = $patch -> {'for_node'};
  $data{'for_node_nodetype'} = $targetNodeType -> {'title'};
  $data{'for_node_title'} = $targetNode -> {'title'};
  $data{'field'} = $patch -> {'field'};
  $data{'purpose'} = $patch -> {'purpose'};
  $data{'instructions'} = $patch -> {'instructions'};
  $data{'author'} = getNodeById($patch -> {author_user}) -> {'title'};
  $data{'patch_id'} = $patch -> {'patch_id'};

  #Remember that an applied patch like a production-ready patch will have its
  #code field swapped with the target node's.
  $data{'code'} = [ $patchCode ]; 
  $data{'target_hash'} = md5_hex($patch -> {'code'} );
  push @xmlPatches, \%data ;
}

# For reason behind options, see http://perldesignpatterns.com/?XmlSimple
my $xmls = XML::Simple->new( 
    RootName => undef, 
    KeepRoot => 1, 
    ForceArray => 1,
    ForceContent => 1,
);

my $xml = $xmls->XMLout(
   {
     'e2patchset' =>
     { 'patch' => \@xmlPatches }
   }
);

return $xml;

%]
